   192    private fun setupGestureDetector() {
   193        val gestureListener = object : GestureDetector.SimpleOnGestureListener() {
   194            private val swipeThreshold = 100
   195            private val swipeVelocityThreshold = 100
   196
   197            override fun onDown(e: MotionEvent): Boolean {
   198                return true
   199            }
   200
   201            override fun onFling(
   202                e1: MotionEvent?,
   203                e2: MotionEvent,
   204                velocityX: Float,
   205                velocityY: Float
   206            ): Boolean {
   207                if (e1 == null) {
   208                    return false
   209                }
   210                val dx = e2.x - e1.x
   211                val dy = e2.y - e1.y
   212                var result = false
   213                try {
   214                    L.d("Gesture: dx=${dx.toInt()}, dy=${dy.toInt()}, velX=${velocityX.toInt()}, mapScrolling=${nativeMapHandler.isHorizontalScrollInProgress}")
   215                    
   216                    if (nativeMapHandler.isHorizontalScrollInProgress) {
   217                        L.d("Gesture: Blocked by map horizontal scroll")
   218                        return false
   219                    }
   220                    if (abs(dx) > abs(dy) &&
   221                        abs(dx) > swipeThreshold &&
   222                        abs(velocityX) > swipeVelocityThreshold
   223                    ) {
   224                        val direction = if (dx > 0) "START" else "END"
   225                        L.d("Gesture: Valid swipe detected, direction=$direction")
   226
   227                        if (dx > 0) {
   228                            callback?.onPageSwipe(Gravity.START)
   229                        } else {
   230                            callback?.onPageSwipe(Gravity.END)
   231                        }
   232                        result = true
   233                    } else {
   234                        L.d("Gesture: Failed thresholds - dx_vs_dy=${abs(dx) > abs(dy)}, dx_threshold=${abs(dx) > swipeThreshold}, vel_threshold=${abs(velocityX) > swipeVelocityThreshold}")
   235                    }
   236                } catch (exception: Exception) {
   237                    L.e("Error during swipe detection.", exception)
   238                }
   239                return result
   240            }
   241        }
   242        gestureDetector = GestureDetector(requireContext(), gestureListener)
   243        binding.pageWebView.setOnTouchListener { _, event ->
   244            // If JS signaled an active horizontal interaction (e.g., Highcharts),
   245            // do not feed events into the back-swipe GestureDetector.
   246            if (::nativeMapHandler.isInitialized && nativeMapHandler.isHorizontalScrollInProgress) {
   247                return@setOnTouchListener false
   248            }
   249            gestureDetector.onTouchEvent(event)
   250            false
   251        }
   252    }
