   254    private fun fetchTableOfContents() {
   255        val script = """
   256            (function() {
   257                var tocData = { leadSectionDetails: null, sections: [] };
   258                var headerSpans = document.querySelectorAll('.mw-headline');
   259                for (var i = 0; i < headerSpans.length; i++) {
   260                    var span = headerSpans[i];
   261                    var header = span.parentElement;
   262                    if (span.id && (header.tagName === 'H2' || header.tagName === 'H3')) {
   263                        var level = parseInt(header.tagName.substring(1));
   264                        var computedStyle = window.getComputedStyle(span);
   265                        tocData.sections.push({
   266                            id: i + 1, level: level, anchor: span.id, title: span.textContent.trim(),
   267                            isItalic: computedStyle.fontStyle === 'italic',
   268                            isBold: parseInt(computedStyle.fontWeight) >= 700 || computedStyle.fontWeight === 'bold' || computedStyle.fontWeight === 'bolder'
   269                        });
   270                    }
   271                }
   272                var leadHeader = document.querySelector('h1.page-header');
   273                if (leadHeader) {
   274                    var leadStyle = window.getComputedStyle(leadHeader);
   275                    tocData.leadSectionDetails = {
   276                        title: leadHeader.textContent.trim(),
   277                        isItalic: leadStyle.fontStyle === 'italic',
   278                        isBold: parseInt(leadStyle.fontWeight) >= 700 || leadStyle.fontWeight === 'bold' || leadStyle.fontWeight === 'bolder'
   279                    };
   280                }
   281                return JSON.stringify(tocData);
   282            })();
   283        """.trimIndent()
   284        binding.pageWebView.evaluateJavascript(script) { jsonString ->
   285            // Coroutine to move parsing off the main thread.
   286            viewLifecycleOwner.lifecycleScope.launch {
   287                if (jsonString != null && jsonString != "null" && jsonString != "\"\"") {
   288                    try {
   289                        val fullToc = withContext(Dispatchers.Default) {
   290                            // Heavy parsing now happens on a background thread.
   291                            val unescapedJson = Json.decodeFromString<String>(jsonString)
   292                            val tocData = Json.decodeFromString<TocData>(unescapedJson)
   293
   294                            val leadSection = tocData.leadSectionDetails?.let {
   295                                Section(0, 1, "", it.title, it.isItalic, it.isBold)
   296                            } ?: run {
   297                                val fallbackTitle =
   298                                    (activity as? AppCompatActivity)?.supportActionBar?.title?.toString() ?: "Top of page"
   299                                Section(0, 1, "", fallbackTitle, isItalic = false, isBold = true)
   300                            }
   301                            mutableListOf(leadSection).apply { addAll(tocData.sections) }
   302                        }
   303                        // Switch back to the main thread to update the UI.
   304                        contentsHandler.setup(fullToc)
   305                    } catch (e: Exception) {
   306                        L.e("Failed to parse TOC JSON", e)
   307                    }
   308                }
   309            }
   310        }
   311    }
