    24‚Üí            const images = container.querySelectorAll('img');
    25‚Üí            images.forEach(img => {
    26‚Üí                const src = img.getAttribute('src');
    27‚Üí                if (src) { imageUrlsToPreload.add(src); }
    28‚Üí                const srcset = img.getAttribute('srcset');
    29‚Üí                if (srcset) {
    30‚Üí                    const sources = srcset.split(',').map(s => s.trim().split(/\s+/)[0]);
    31‚Üí                    sources.forEach(sourceUrl => imageUrlsToPreload.add(sourceUrl));
    32‚Üí                }
    33‚Üí            });
    34‚Üí        });
    35‚Üí
    36‚Üí        imageUrlsToPreload.forEach(url => {
    37‚Üí            const preloader = new Image();
    38‚Üí            preloader.src = url;
    39‚Üí            preloader.decode().catch(() => {});
    40‚Üí        });
    41‚Üí        // --- END PRELOADER ---
    42‚Üí
    43‚Üí        document.body.addEventListener('click', (event) => {
    44‚Üí            const button = event.target.closest('.button');
    45‚Üí            if (button && button.hasAttribute('data-switch-index')) {
    46‚Üí                const switchIndex = button.getAttribute('data-switch-index');
    47‚Üí                performSwitch(switchIndex);
    48‚Üí            }
    49‚Üí        });
    50‚Üí
