     1‚Üí/*
     2‚Üí        Styles for the OSRS Wiki infobox.
     3‚Üí        These rules use the app's theme-aware variables.
     4‚Üí*/
     5‚Üí
     6‚Üí/* Base styling for all infobox containers. */
     7‚Üí.infobox {
     8‚Üí        background: var(--wikitable-bg);
     9‚Üí        border: 1px solid var(--wikitable-border);
    10‚Üí        font-size: 0.85em;
    11‚Üí        margin: 0; /* Margin is handled by the collapsible content container */
    12‚Üí        border-collapse: collapse;
    13‚Üí        box-shadow: 0 0.25rem 0.35rem -0.25rem rgba(0,0,0,0.1);
    14‚Üí        word-break: break-word;
    15‚Üí}
    16‚Üí
    17‚Üí/* The main infobox should fill its container. */
    18‚Üí.collapsible-container .main-infobox {
    19‚Üí        width: 100%;
    20‚Üí        table-layout: fixed;
    21‚Üí}
    22‚Üí
    23‚Üí/*
    24‚Üí        `table-layout: fixed` ensures all columns are of uniform width.
    25‚Üí        A total width is set to constrain the layout algorithm.
    26‚Üí*/
    27‚Üí.infobox.infobox-bonuses {
    28‚Üí        table-layout: fixed;
    29‚Üí        max-width: none;
    30‚Üí        /* Total width is the sum of the desired column widths:
    31‚Üí                       250px (bonuses) + 153px (image1) + 153px (image2) = 556px */
    32‚Üí        width: 556px;
    33‚Üí}
    34‚Üí
    35‚Üí/* Generic rule for all images in an infobox. */
    36‚Üí.infobox img {
    37‚Üí        margin: 0 auto; /* Center the image horizontally */
    38‚Üí        border-radius: 0;
    39‚Üí        max-width: 100%; /* Prevent image from overflowing its container */
    40‚Üí        width: auto; /* Allow image to use its natural width */
    41‚Üí        height: auto;
    42‚Üí        display: block;
    43‚Üí}
    44‚Üí
    45‚Üí/*
    46‚Üí        For small icons, we only need to ensure they are centered.
    47‚Üí        They do not need `max-width: none`.
    48‚Üí*/
    49‚Üí.infobox .infobox-nested img,
    50‚Üí.infobox .infobox-subheader img {
    51‚Üí        display: inline-block;
    52‚Üí        vertical-align: middle;
    53‚Üí}
    54‚Üí
    55‚Üí/*
    56‚Üí        For large equipment images, `max-width: none` is the key to forcing
    57‚Üí        the table to expand and enable horizontal scrolling.
    58‚Üí*/
    59‚Üí.infobox .infobox-bonuses-image img {
    60‚Üí        display: inline-block;
    61‚Üí        max-width: none;
    62‚Üí        vertical-align: middle;
    63‚Üí}
    64‚Üí
    65‚Üí.mw-kartographer-map {
    66‚Üí        box-sizing: border-box !important;
    67‚Üí        padding: 0 !important;
    68‚Üí        border: none !important;
    69‚Üí        width: 100% !important;
    70‚Üí        min-height: 200px;
    71‚Üí        display: flex;
    72‚Üí        align-items: center;
    73‚Üí        justify-content: center;
    74‚Üí}
    75‚Üí
    76‚Üí/* General styling for header and data cells */
    77‚Üí.infobox > tbody > tr > td,
    78‚Üí.infobox > tbody > tr > th {
    79‚Üí        background-color: transparent;
    80‚Üí        color: var(--wikitable-color);
    81‚Üí        padding: 0.25em 0.5em;
    82‚Üí        line-height: 1.6em;
    83‚Üí        text-align: left;
    84‚Üí        vertical-align: top;
    85‚Üí}
    86‚Üí
    87‚Üí.infobox .infobox-header,
    88‚Üí.infobox .infobox-subheader {
    89‚Üí        background-color: var(--wikitable-header-bg);
    90‚Üí        color: var(--wikitable-color);
    91‚Üí        font-weight: bold;
    92‚Üí        text-align: center;
    93‚Üí}
    94‚Üí
    95‚Üí.infobox .infobox-header {
    96‚Üí        font-size: 1.15em;
    97‚Üí        line-height: 1.4em;
    98‚Üí        padding: 0.5em 0;
    99‚Üí}
   100‚Üí
   101‚Üí.infobox .infobox-subheader {
   102‚Üí        line-height: 2em;
   103‚Üí        /* Set an explicit width on the subheader that spans the icon columns.
   104‚Üí                       In a fixed-layout table, this width is distributed to the columns it spans. */
   105‚Üí        width: 250px;
   106‚Üí}
   107‚Üí
   108‚Üí.infobox th:not(.infobox-header):not(.infobox-subheader):not(.infobox-nested) {
   109‚Üí        text-align: right;
   110‚Üí        border-right: 2px solid var(--colorsurfacevariant);
   111‚Üí}
   112‚Üí
   113‚Üí.infobox .infobox-full-width-content {
   114‚Üí        padding: 0;
   115‚Üí        text-align: center;
   116‚Üí}
   117‚Üí
   118‚Üí/*
   119‚Üí        ==============================================================================
   120‚Üí        Styles for generic collapsible containers.
   121‚Üí        ==============================================================================
   122‚Üí*/
   123‚Üí
   124‚Üí.collapsible-container {
   125‚Üí        border-radius: 4px;
   126‚Üí        /* Remove forced dimensions - let child determine size */
   127‚Üí        width: auto;
   128‚Üí        /* No max-width override - child content controls size */
   129‚Üí        overflow: hidden; /* This clips the corners for the border-radius */
   130‚Üí        background-color: var(--colorsurfacevariant);
   131‚Üí        color: var(--coloronsurfacevariant);
   132‚Üí        display: block;
   133‚Üí        /* Default fallback for unmatched content */
   134‚Üí        margin: 1em 0;
   135‚Üí        width: fit-content;
   136‚Üí        /* margin and float handled by specific rules below */
   137‚Üí}
   138‚Üí
   139‚Üí/* True transparent wrapper behavior - containers inherit exact child positioning */
   140‚Üí
   141‚Üí/* Float Right: Match child's right-floating behavior exactly */
   142‚Üí.collapsible-container:has([class*="floatright"], [class*="-right"], .archivelist, .shortcut, .mw-halign-right, .infobox:not(.skill-info):not(.infobox-bonuses), .multi-infobox) {
   143‚Üí        float: right;
   144‚Üí        margin: 0.5em 0 1em 1em; /* Match typical right-floating margin */
   145‚Üí        width: fit-content; /* Wrap around child exactly */
   146‚Üí}
   147‚Üí
   148‚Üí/* Float Left: Match child's left-floating behavior exactly */
   149‚Üí.collapsible-container:has([class*="floatleft"], [class*="-left"], .mw-halign-left) {
   150‚Üí        float: left;
   151‚Üí        margin: 0.5em 1em 1em 0; /* Match typical left-floating margin */
   152‚Üí        width: fit-content; /* Wrap around child exactly */
   153‚Üí}
   154‚Üí
   155‚Üí/* No Float: Match child's normal document flow positioning */
   156‚Üí.collapsible-container:has(.infobox.skill-info, .infobox-bonuses, [class*="-center"], .mw-halign-center, .mw-halign-none) {
   157‚Üí        float: none;
   158‚Üí        margin: 1em 0; /* Normal document flow - NOT centered */
   159‚Üí        width: fit-content; /* Wrap around child exactly */
   160‚Üí}
   161‚Üí
   162‚Üí/* Full Width Content (navboxes, large tables) */
   163‚Üí.collapsible-container:has(.navbox, .wikitable) {
   164‚Üí        width: 100%; /* Match full-width content */
   165‚Üí        margin: 1em auto 0; /* Match navbox margins */
   166‚Üí        float: none;
   167‚Üí}
   168‚Üí
   169‚Üí/* ========================================================================
   170‚Üí   Mobile Responsive Behavior - Override floating on narrow screens
   171‚Üí   Matches MediaWiki's body.skin--responsive responsive system
   172‚Üí   ========================================================================*/
   173‚Üí@media screen and (max-width: 768px) {
   174‚Üí        /* Override all floating containers to be non-floating on mobile */
   175‚Üí        .collapsible-container:has([class*="floatright"], [class*="-right"], .archivelist, .shortcut, .mw-halign-right, .infobox:not(.skill-info):not(.infobox-bonuses), .multi-infobox),
   176‚Üí        .collapsible-container:has([class*="floatleft"], [class*="-left"], .mw-halign-left) {
   177‚Üí                float: none !important;
   178‚Üí                clear: both !important;
   179‚Üí                margin: 1em auto !important; /* Center on mobile like web */
   180‚Üí                width: fit-content;
   181‚Üí        }
   182‚Üí        
   183‚Üí        /* Ensure all containers are properly centered on mobile */
   184‚Üí        .collapsible-container {
   185‚Üí                margin-left: auto;
   186‚Üí                margin-right: auto;
   187‚Üí        }
   188‚Üí}
   189‚Üí
   190‚Üí
   191‚Üí.collapsible-header {
   192‚Üí        display: flex;
   193‚Üí        align-items: center;
