package com.omiyawaki.osrswiki.settings

import android.content.Context
import android.util.Log
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.OSRSWikiApp
import com.omiyawaki.osrswiki.theme.Theme
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.coroutines.sync.Mutex
import kotlinx.coroutines.sync.withLock
import kotlinx.coroutines.delay
import kotlinx.coroutines.CancellationException
import kotlinx.coroutines.withTimeout
import kotlinx.coroutines.TimeoutCancellationException
import java.util.concurrent.atomic.AtomicBoolean

/**
 * Manages background generation of table collapse and theme previews to eliminate loading latency.
 * 
 * This service pre-generates previews on app launch and handles smart theme switching
 * by generating new theme previews before clearing old ones, ensuring seamless transitions.
 */
object PreviewGenerationManager {

    private const val TAG = "PreviewGenerationMgr"
    private const val MAX_RETRY_ATTEMPTS = 3
    private const val RETRY_DELAY_MS = 2000L // 2 seconds between retries
    private const val GENERATION_TIMEOUT_MS = 45000L // 45 seconds total timeout (increased)
    private const val SINGLE_PREVIEW_TIMEOUT_MS = 20000L // 20 seconds per preview (increased)
    // Removed arbitrary startup delay - we'll use Activity readiness signaling instead

    // Will use Application scope for generation that survives Activity lifecycle

    // Mutex to ensure only one generation process runs at a time
    private val generationMutex = Mutex()

    // Track generation state
    private val isInitialized = AtomicBoolean(false)

    // Track what has been generated to avoid duplicates
    private val generatedTablePreviews = mutableSetOf<String>()
    private val generatedThemePreviews = mutableSetOf<String>()

    /**
     * Initialize background preview generation using Application-level scope.
     * Uses scope that survives Activity lifecycle changes.
     * Safe to call multiple times - will only run once.
     * 
     * CRITICAL FIX: This is now a suspend function that waits for completion
     * instead of launching work and returning immediately (fire-and-forget).
     */
    suspend fun initializeBackgroundGeneration(app: OSRSWikiApp, currentTheme: Theme) {
        if (!isInitialized.compareAndSet(false, true)) {
            Log.d(TAG, "Background generation already initialized")
            return
        }

        Log.i("StartupTiming", "PreviewGenerationManager.initializeBackgroundGeneration() - Starting background preview generation for theme: ${currentTheme.tag}")
        Log.d(TAG, "Starting background preview generation for theme: ${currentTheme.tag}")

        try {
            withTimeout(GENERATION_TIMEOUT_MS) {
                generationMutex.withLock {
                    Log.i("StartupTiming", "PreviewGenerationManager - Starting actual generation work")

                    // Phase 1: Generate current theme previews (high priority for instant loading)
                    generateCurrentThemePreviews(app, currentTheme)

                    // Phase 2: Generate alternative theme previews (low priority, nice-to-have)
                    generateAlternativeThemePreviews(app, currentTheme)

                    Log.i("StartupTiming", "PreviewGenerationManager - All generation work completed")
                }
            }

            // CRITICAL: Add disk flush verification to ensure cache is durable
            Log.i("StartupTiming", "PreviewGenerationManager - Starting disk flush verification")
            val flushStartTime = System.currentTimeMillis()

            // Force disk flush by attempting cache verification
            try {
                // Verify cache files exist on disk by checking both themes and table previews
                val cacheVerified = verifyPreviewCachesOnDisk(app, currentTheme)
                val flushDuration = System.currentTimeMillis() - flushStartTime
                Log.i("StartupTiming", "DISK_FLUSH_COMPLETE cache_verified=${cacheVerified} duration=${flushDuration}ms")
            } catch (e: Exception) {
                Log.w("StartupTiming", "DISK_FLUSH_VERIFICATION_FAILED: ${e.message}")
            }

            Log.i("StartupTiming", "PreviewGenerationManager.initializeBackgroundGeneration() - COMPLETED after real work")
            Log.d(TAG, "Background preview generation completed successfully")

        } catch (e: TimeoutCancellationException) {
            Log.e(TAG, "Background preview generation timed out after ${GENERATION_TIMEOUT_MS}ms", e)
            Log.i("StartupTiming", "PreviewGenerationManager - TIMEOUT after ${GENERATION_TIMEOUT_MS}ms")
            // Reset initialization flag to allow retry
            isInitialized.set(false)
            throw e
        } catch (e: Exception) {
            Log.e(TAG, "Background preview generation failed", e)
            Log.i("StartupTiming", "PreviewGenerationManager - FAILED: ${e.message}")
            // Reset initialization flag to allow retry
            isInitialized.set(false)
            throw e
        }
    }

    /**
     * Handle theme changes with smart cache swapping to eliminate switch latency.
     * Generates new theme previews in background, then swaps atomically.
     */
    suspend fun handleThemeChange(app: OSRSWikiApp, newTheme: Theme) {
        Log.d(TAG, "Handling theme change to: ${newTheme.tag}")

        try {
            generationMutex.withLock {
                // Generate new theme previews in background (keeping old ones available)
                generateThemeSpecificPreviews(app, newTheme)

                // Old previews are automatically superseded by the new cache keys
                // No need to explicitly clear - the cache system handles this
                Log.d(TAG, "Theme change completed - new previews ready")
            }
        } catch (e: Exception) {
            Log.e(TAG, "Theme change handling failed", e)
            // Fallback: clear caches and regenerate (original behavior)
            withContext(Dispatchers.Main) {
                TablePreviewRenderer.clearCache(app)
                ThemePreviewRenderer.clearCache(app)
            }
        }
    }

    /**
     * Retry wrapper that handles cancellation exceptions with exponential backoff.
     * Specifically designed to handle JobCancellationException during WebView rendering.
     */
    private suspend fun <T> withRetry(
        operationName: String,
        operation: suspend () -> T
    ): T {
        var lastException: Exception? = null

        repeat(MAX_RETRY_ATTEMPTS) { attempt ->
            try {
                Log.d(TAG, "Executing $operationName (attempt ${attempt + 1}/$MAX_RETRY_ATTEMPTS)")
                return operation()
            } catch (e: CancellationException) {
                lastException = e
                Log.w(TAG, "$operationName cancelled on attempt ${attempt + 1}/$MAX_RETRY_ATTEMPTS: ${e.message}")

                if (attempt < MAX_RETRY_ATTEMPTS - 1) {
                    val delayMs = RETRY_DELAY_MS * (attempt + 1) // Exponential backoff
                    Log.d(TAG, "Retrying $operationName in ${delayMs}ms...")
                    delay(delayMs)
                } else {
                    Log.e(TAG, "$operationName failed after $MAX_RETRY_ATTEMPTS attempts")
                    throw e
                }
            } catch (e: Exception) {
                // Non-cancellation exceptions should fail immediately
                Log.e(TAG, "$operationName failed with non-cancellation exception", e)
                throw e
            }
        }

        // Should never reach here, but just in case
        throw lastException ?: Exception("Unknown retry failure")
    }

    /**
     * Generate previews for the current theme (high priority).
     */
    private suspend fun generateCurrentThemePreviews(app: OSRSWikiApp, currentTheme: Theme) {
        Log.i(TAG, "GENERATION SEQUENCING: Phase 1 starting - Current theme previews (${currentTheme.tag})")
        Log.i("StartupTiming", "PHASE1_START current_theme=${currentTheme.tag}")
        val phase1StartTime = System.currentTimeMillis()

        try {
            // Generate table collapse previews for current theme
            Log.d(TAG, "GENERATION SEQUENCING: Starting table previews for current theme ${currentTheme.tag}")
            generateTablePreviews(app, currentTheme)
            Log.d(TAG, "GENERATION SEQUENCING: Completed table previews for current theme ${currentTheme.tag}")

            // Generate theme preview for current theme
            Log.d(TAG, "GENERATION SEQUENCING: Starting theme preview for current theme ${currentTheme.tag}")
            generateThemePreview(app, currentTheme)
            Log.d(TAG, "GENERATION SEQUENCING: Completed theme preview for current theme ${currentTheme.tag}")

        } catch (e: Exception) {
            Log.e(TAG, "GENERATION SEQUENCING: Phase 1 failed for theme ${currentTheme.tag}", e)
            Log.i("StartupTiming", "PHASE1_FAILED current_theme=${currentTheme.tag} error=${e.message}")
            throw e
        }

        val phase1Duration = System.currentTimeMillis() - phase1StartTime
        Log.i("StartupTiming", "PHASE1_COMPLETE current_theme=${currentTheme.tag} duration=${phase1Duration}ms")
        Log.i(TAG, "GENERATION SEQUENCING: Phase 1 completed - Current theme previews (${currentTheme.tag})")
    }

    /**
     * Generate previews for alternative themes (low priority).
     */
    private suspend fun generateAlternativeThemePreviews(app: OSRSWikiApp, currentTheme: Theme) {
        val alternativeTheme = when (currentTheme) {
            Theme.OSRS_LIGHT -> Theme.OSRS_DARK
            Theme.OSRS_DARK -> Theme.OSRS_LIGHT
        }

        Log.i(TAG, "GENERATION SEQUENCING: Phase 2 starting - Alternative theme previews (current: ${currentTheme.tag} -> alternative: ${alternativeTheme.tag})")
        Log.i("StartupTiming", "PHASE2_START alternative_theme=${alternativeTheme.tag}")
        val phase2StartTime = System.currentTimeMillis()

        try {
            // Generate table collapse previews for alternative theme
            Log.d(TAG, "GENERATION SEQUENCING: Starting table previews for alternative theme ${alternativeTheme.tag}")
            generateTablePreviews(app, alternativeTheme)
            Log.d(TAG, "GENERATION SEQUENCING: Completed table previews for alternative theme ${alternativeTheme.tag}")

            // Generate theme preview for alternative theme  
            Log.d(TAG, "GENERATION SEQUENCING: Starting theme preview for alternative theme ${alternativeTheme.tag}")
            generateThemePreview(app, alternativeTheme)
            Log.d(TAG, "GENERATION SEQUENCING: Completed theme preview for alternative theme ${alternativeTheme.tag}")

        } catch (e: Exception) {
            Log.e(TAG, "GENERATION SEQUENCING: Phase 2 failed for alternative theme ${alternativeTheme.tag}", e)
            Log.i("StartupTiming", "PHASE2_FAILED alternative_theme=${alternativeTheme.tag} error=${e.message}")
            throw e
        }

        val phase2Duration = System.currentTimeMillis() - phase2StartTime
        Log.i("StartupTiming", "PHASE2_COMPLETE alternative_theme=${alternativeTheme.tag} duration=${phase2Duration}ms")
        Log.i(TAG, "GENERATION SEQUENCING: Phase 2 completed - Alternative theme previews (${alternativeTheme.tag})")
    }

    /**
     * Generate all previews for a specific theme (used during theme changes).
     */
    private suspend fun generateThemeSpecificPreviews(app: OSRSWikiApp, theme: Theme) {
        Log.d(TAG, "Generating theme-specific previews for: ${theme.tag}")

        // Generate table collapse previews
        generateTablePreviews(app, theme)

        // Generate theme preview
        generateThemePreview(app, theme)
    }

    /**
     * Generate table collapse previews (collapsed + expanded) for a theme.
     */
    private suspend fun generateTablePreviews(app: OSRSWikiApp, theme: Theme) {
        val collapsedKey = "table-${theme.tag}-collapsed"
        val expandedKey = "table-${theme.tag}-expanded"

        // Skip if already generated
        if (generatedTablePreviews.contains(collapsedKey) && 
            generatedTablePreviews.contains(expandedKey)) {
            Log.d(TAG, "Table previews for ${theme.tag} already generated, skipping")
            Log.i("StartupTiming", "TABLE_PREVIEW_SKIPPED theme=${theme.tag} (already_generated)")
            return
        }

        try {
            Log.d(TAG, "Generating table previews for theme: ${theme.tag}")
            Log.i("StartupTiming", "TABLE_PREVIEW_START theme=${theme.tag}")
            val tableStartTime = System.currentTimeMillis()

            // Generate collapsed state preview with retry logic and timeout
            if (!generatedTablePreviews.contains(collapsedKey)) {
                Log.i("StartupTiming", "TABLE_PREVIEW_COLLAPSED_START theme=${theme.tag}")
                val collapsedStartTime = System.currentTimeMillis()

                withTimeout(SINGLE_PREVIEW_TIMEOUT_MS) {
                    withRetry("Table preview generation (collapsed, ${theme.tag})") {
                        TablePreviewRenderer.getPreview(app, true, theme)
                    }
                }

                val collapsedDuration = System.currentTimeMillis() - collapsedStartTime
                Log.i("StartupTiming", "TABLE_PREVIEW_COLLAPSED_COMPLETE theme=${theme.tag} duration=${collapsedDuration}ms")
                generatedTablePreviews.add(collapsedKey)
                Log.d(TAG, "Generated collapsed table preview for ${theme.tag}")
            }

            // Generate expanded state preview with retry logic and timeout
            if (!generatedTablePreviews.contains(expandedKey)) {
                Log.i("StartupTiming", "TABLE_PREVIEW_EXPANDED_START theme=${theme.tag}")
                val expandedStartTime = System.currentTimeMillis()

                withTimeout(SINGLE_PREVIEW_TIMEOUT_MS) {
                    withRetry("Table preview generation (expanded, ${theme.tag})") {
                        TablePreviewRenderer.getPreview(app, false, theme)
                    }
                }

                val expandedDuration = System.currentTimeMillis() - expandedStartTime
                Log.i("StartupTiming", "TABLE_PREVIEW_EXPANDED_COMPLETE theme=${theme.tag} duration=${expandedDuration}ms")
                generatedTablePreviews.add(expandedKey)
                Log.d(TAG, "Generated expanded table preview for ${theme.tag}")
            }

            val totalTableDuration = System.currentTimeMillis() - tableStartTime
            Log.i("StartupTiming", "TABLE_PREVIEW_COMPLETE theme=${theme.tag} total_duration=${totalTableDuration}ms")

        } catch (e: Exception) {
            Log.e(TAG, "Failed to generate table previews for ${theme.tag}", e)
            Log.i("StartupTiming", "TABLE_PREVIEW_FAILED theme=${theme.tag} error=${e.message}")
        }
    }

    /**
     * Generate theme preview for a theme.
     */
    private suspend fun generateThemePreview(app: OSRSWikiApp, theme: Theme) {
        val themeKey = "theme-${theme.tag}"

        // Skip if already generated
        if (generatedThemePreviews.contains(themeKey)) {
            Log.d(TAG, "Theme preview for ${theme.tag} already generated, skipping")
            Log.i("StartupTiming", "THEME_PREVIEW_SKIPPED theme=${theme.tag} (already_generated)")
            return
        }

        try {
            Log.d(TAG, "Generating theme preview for: ${theme.tag}")
            Log.i("StartupTiming", "THEME_PREVIEW_START theme=${theme.tag}")
            val themeStartTime = System.currentTimeMillis()

            // Generate theme preview based on theme type with retry logic and timeout
            withTimeout(SINGLE_PREVIEW_TIMEOUT_MS) {
                withRetry("Theme preview generation (${theme.tag})") {
                    when (theme) {
                        Theme.OSRS_LIGHT -> ThemePreviewRenderer.getPreview(
                            app, 
                            R.style.Theme_OSRSWiki_OSRSLight, 
                            "light"
                        )
                        Theme.OSRS_DARK -> ThemePreviewRenderer.getPreview(
                            app, 
                            R.style.Theme_OSRSWiki_OSRSDark, 
                            "dark"
                        )
                    }
                }
            }

            val themeDuration = System.currentTimeMillis() - themeStartTime
            Log.i("StartupTiming", "THEME_PREVIEW_COMPLETE theme=${theme.tag} duration=${themeDuration}ms")
            generatedThemePreviews.add(themeKey)
            Log.d(TAG, "Generated theme preview for ${theme.tag}")

        } catch (e: Exception) {
            Log.e(TAG, "Failed to generate theme preview for ${theme.tag}", e)
            Log.i("StartupTiming", "THEME_PREVIEW_FAILED theme=${theme.tag} error=${e.message}")
        }
    }

    /**
     * Cancel any ongoing background generation (for app lifecycle management).
     * NOTE: Since generation is now synchronous within WorkManager, 
     * this primarily resets state for cleanup.
     */
    fun cancelGeneration() {
        Log.d(TAG, "Resetting preview generation state (generation is now synchronous)")
        generatedTablePreviews.clear()
        generatedThemePreviews.clear()
        isInitialized.set(false)
    }

    /**
     * Reset generation state (for testing or manual cache clearing).
     */
    fun resetState() {
        Log.d(TAG, "Resetting preview generation state")
        generatedTablePreviews.clear()
        generatedThemePreviews.clear()
        isInitialized.set(false)
    }

    /**
     * Verify that preview caches are actually written to disk.
     * This ensures WorkManager completion represents real cache durability.
     * Simplified version that checks cache directory existence and basic file I/O.
     */
    private suspend fun verifyPreviewCachesOnDisk(app: OSRSWikiApp, currentTheme: Theme): Boolean {
        return withContext(Dispatchers.IO) {
            try {
                // Check cache directories exist and are writable
                val tableCacheDir = java.io.File(app.cacheDir, "table_previews")
                val themeCacheDir = java.io.File(app.cacheDir, "theme_previews")

                val tableCacheDirExists = tableCacheDir.exists() && tableCacheDir.isDirectory && tableCacheDir.canRead()
                val themeCacheDirExists = themeCacheDir.exists() && themeCacheDir.isDirectory && themeCacheDir.canRead()

                // Count cache files in directories
                val tableCacheFileCount = if (tableCacheDirExists) {
                    tableCacheDir.listFiles()?.size ?: 0
                } else 0

                val themeCacheFileCount = if (themeCacheDirExists) {
                    themeCacheDir.listFiles()?.size ?: 0
                } else 0

                val totalCacheFiles = tableCacheFileCount + themeCacheFileCount

                Log.i("StartupTiming", "CACHE_VERIFICATION " +
                        "table_cache_dir_exists=${tableCacheDirExists} " +
                        "theme_cache_dir_exists=${themeCacheDirExists} " +
                        "table_cache_files=${tableCacheFileCount} " +
                        "theme_cache_files=${themeCacheFileCount} " +
                        "total_cache_files=${totalCacheFiles}")

                // Return true if we have any cache files (indicating generation occurred)
                totalCacheFiles > 0

            } catch (e: Exception) {
                Log.e(TAG, "Cache verification failed", e)
                Log.i("StartupTiming", "CACHE_VERIFICATION_FAILED error=${e.message}")
                false
            }
        }
    }

    /**
     * Get current generation status for debugging.
     */
    fun getGenerationStatus(): Map<String, Any> {
        return mapOf(
            "initialized" to isInitialized.get(),
            "generatedTablePreviews" to generatedTablePreviews.toList(),
            "generatedThemePreviews" to generatedThemePreviews.toList(),
            "generationType" to "synchronous_workmanager"
        )
    }
}

