/**
 * Toggles theme via JavaScript with event-driven callback
 */
private fun toggleTheme(
    webView: ObservableWebView, 
    theme: String, 
    callback: (Boolean) -> Unit,
    pendingThemeChangeCallback: ((Boolean) -> Unit)?
) {
    Log.d(TAG, "Toggling theme to: $theme")

    // Store the callback for when JavaScript calls back
    this.pendingThemeChangeCallback = callback

    webView.evaluateJavascript("window.setTheme('$theme')") { result ->
        val success = result == "true"
        Log.d(TAG, "Theme toggle JavaScript result: $result (success: $success)")

        if (!success) {
            // If JavaScript failed, clear callback and notify failure
            this.pendingThemeChangeCallback = null
            callback(false)
        }
        // If successful, callback will be triggered by JavaScript via onThemeChangeComplete()
    }
}

