                                }
                                Log.d("PreviewDiagnosis", "âœ“ Expert validation passed: bitmap=${bmp.width}x${bmp.height} matches view=${imageView.width}x${imageView.height}")
                            } catch (e: IllegalStateException) {
                                Log.e("PreviewDiagnosis", "âœ— ${e.message}")
