package com.omiyawaki.osrswiki.settings

import android.os.Bundle
import android.view.View
import android.widget.TextView
import androidx.preference.ListPreference
import androidx.preference.Preference
import androidx.preference.PreferenceFragmentCompat
import androidx.recyclerview.widget.RecyclerView
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.util.applyAlegreyaHeadline
import com.omiyawaki.osrswiki.util.applyRubikUILabel
import com.omiyawaki.osrswiki.util.log.L

class SettingsFragment : PreferenceFragmentCompat() {

    override fun onCreatePreferences(savedInstanceState: Bundle?, rootKey: String?) {
        setPreferencesFromResource(R.xml.preferences, rootKey)

        val appThemeModePref = findPreference<ListPreference>(Prefs.KEY_APP_THEME_MODE)

        // Theme change listener for app theme mode preference.
        val themeChangeListener = Preference.OnPreferenceChangeListener { _, _ ->
            // Post the recreate call to the view's message queue. This ensures the
            // preference value has time to be persisted before the activity is destroyed,
            // and provides immediate visual feedback for the theme change.
            view?.post {
                activity?.recreate()
            }

            // Return true to allow the preference change to be saved.
            true
        }

        // Assign the listener to the app theme mode preference.
        appThemeModePref?.onPreferenceChangeListener = themeChangeListener
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupFonts()
    }


    private fun setupFonts() {
        L.d("SettingsFragment: Setting up fonts...")

        // Apply fonts to preference TextViews by traversing the RecyclerView
        val recyclerView = listView as? RecyclerView
        recyclerView?.post {
            applyFontsToPreferences(recyclerView)
        }
    }

    private fun applyFontsToPreferences(recyclerView: RecyclerView) {
        for (i in 0 until recyclerView.childCount) {
            val itemView = recyclerView.getChildAt(i)
            applyFontsToPreferenceItem(itemView)
        }

        // Also set up a scroll listener to apply fonts to newly visible items
        recyclerView.addOnScrollListener(object : RecyclerView.OnScrollListener() {
            override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
                super.onScrolled(recyclerView, dx, dy)
                for (i in 0 until recyclerView.childCount) {
                    val itemView = recyclerView.getChildAt(i)
                    applyFontsToPreferenceItem(itemView)
                }
            }
        })

        L.d("SettingsFragment: Fonts applied to preference items")
    }

    private fun applyFontsToPreferenceItem(itemView: View) {
        // Find TextViews in the preference item and apply appropriate fonts
        val title = itemView.findViewById<TextView>(android.R.id.title)
        val summary = itemView.findViewById<TextView>(android.R.id.summary)

        title?.applyAlegreyaHeadline()
        summary?.applyRubikUILabel()
    }

    companion object {
        const val TAG = "OsrsSettingsFragment"
        fun newInstance(): SettingsFragment {
            return SettingsFragment()
        }
    }
}

package com.omiyawaki.osrswiki.settings

import android.content.Context
import android.content.Intent
import android.os.Bundle
import android.widget.TextView
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.activity.BaseActivity // Ensure this import is correct
import com.omiyawaki.osrswiki.util.applyAlegreyaHeadline

/**
 * Activity to host the SettingsFragment for application settings.
 * Mirrors Wikipedia's SettingsActivity pattern by hosting a fragment.
 */
class SettingsActivity : BaseActivity() {

    companion object {
        /**
         * Creates an Intent to start SettingsActivity.
         * @param context The Context to use.
         * @return An Intent to start SettingsActivity.
         */
        fun newIntent(context: Context): Intent {
            return Intent(context, SettingsActivity::class.java)
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_settings)

        if (savedInstanceState == null) {
            supportFragmentManager
                .beginTransaction()
                .replace(R.id.settings_container, SettingsFragment.newInstance(), SettingsFragment.TAG)
                .commit()
        }

        setupToolbar()
        setupFonts()
    }

    private fun setupToolbar() {
        // Assuming BaseActivity sets up a support action bar.
        // If your BaseActivity or themes handle toolbar setup, this might just be for title and Up button.
        supportActionBar?.apply {
            title = getString(R.string.menu_title_settings) // Or a more specific "Settings" string if you have one
            setDisplayHomeAsUpEnabled(true)
            setDisplayShowHomeEnabled(true)
        }
    }

    private fun setupFonts() {
        // Apply fonts to action bar title
        supportActionBar?.let { actionBar ->
            // Find the action bar title TextView and apply font
            val titleId = resources.getIdentifier("action_bar_title", "id", "android")
            if (titleId > 0) {
                findViewById<TextView>(titleId)?.applyAlegreyaHeadline()
            }
        }
    }

    override fun onSupportNavigateUp(): Boolean {
        // Handles the action bar's Up/Home button.
        // If a custom behavior is needed (e.g., specific back stack manipulation), implement it here.
        // Otherwise, default behavior is to finish the activity.
        onBackPressedDispatcher.onBackPressed()
        return true
    }
}

