    35class MapFragment : Fragment(), ThemeAware {
    36
    37    private var _binding: FragmentMapBinding? = null
    38    private val binding get() = _binding!!
    39
    40    private var mapView: MapView? = null
    41    private var map: MapLibreMap? = null
    42    private val logTag = "MapFragment"
    43
    44    private var currentFloor = 0
    45    private val mapFiles = (0..3).map { "map_floor_$it.mbtiles" }
    46    private val maxFloor: Int get() = mapFiles.size - 1
    47
    48    companion object {
    49        private const val ARG_LAT = "arg_lat"
    50        private const val ARG_LON = "arg_lon"
    51        private const val ARG_ZOOM = "arg_zoom"
    52        private const val ARG_PLANE = "arg_plane"
    53        private const val ARG_IS_PRELOADING = "arg_is_preloading"
    54        private const val GROUND_FLOOR_UNDERLAY_OPACITY = 0.5f
    55        private const val DEFAULT_LAT = -25.2023457171692
    56        private const val DEFAULT_LON = -131.44071698586012
    57        private const val DEFAULT_ZOOM = 7.3414426741929
    58
    59        fun newInstance(lat: String?, lon: String?, zoom: String?, plane: String?, isPreloading: Boolean = false): MapFragment {
    60            return MapFragment().apply {
    61                arguments = Bundle().apply {
    62                    putString(ARG_LAT, lat)
    63                    putString(ARG_LON, lon)
    64                    putString(ARG_ZOOM, zoom)
    65                    putString(ARG_PLANE, plane)
    66                    putBoolean(ARG_IS_PRELOADING, isPreloading)
    67                }
    68            }
    69        }
    70
    71        private fun gameToLatLng(gx: Double, gy: Double): LatLng {
    72            val gameCoordScale = 4.0
    73            val gameMinX = 1024.0
    74            val gameMaxY = 12608.0
    75            val canvasSize = 65536.0
    76            val px = (gx - gameMinX) * gameCoordScale
    77            val py = (gameMaxY - gy) * gameCoordScale
    78            val nx = px / canvasSize
    79            val ny = py / canvasSize
    80            val lon = -180.0 + nx * 360.0
    81            val lat = Math.toDegrees(Math.atan(Math.sinh(Math.PI * (1.0 - 2.0 * ny))))
    82            return LatLng(lat, lon)
    83        }
    84    }
    85
    86    override fun onCreate(savedInstanceState: Bundle?) {
    87        super.onCreate(savedInstanceState)
    88        L.d("MapFragment: LIFECYCLE: onCreate")
    89        MapLibre.getInstance(requireContext())
    90        arguments?.getString(ARG_PLANE)?.toIntOrNull()?.let {
    91            currentFloor = it
    92        }
    93    }
    94
    95    override fun onCreateView(
    96        inflater: LayoutInflater,
    97        container: ViewGroup?,
    98        savedInstanceState: Bundle?
    99    ): View {
   100        L.d("MapFragment: LIFECYCLE: onCreateView")
   101        _binding = FragmentMapBinding.inflate(inflater, container, false)
   102        return binding.root
   103    }
   104
   105    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
   106        super.onViewCreated(view, savedInstanceState)
   107        L.d("MapFragment: LIFECYCLE: onViewCreated")
   108        
   109        mapView = binding.mapView
   110
   111        lifecycleScope.launch {
   112            copyAssetsToInternalStorage()
   113            initializeMap(savedInstanceState)
   114        }
   115    }
   116
   117    private suspend fun copyAssetsToInternalStorage() = withContext(Dispatchers.IO) {
   118        for (fileName in mapFiles) {
   119            val destFile = File(requireContext().filesDir, fileName)
   120            if (destFile.exists()) continue
   121            try {
   122                requireContext().assets.open(fileName).use { inputStream ->
   123                    FileOutputStream(destFile).use { outputStream -> inputStream.copyTo(outputStream) }
   124                }
   125            } catch (e: IOException) {
   126                Log.e(logTag, "Failed to copy asset file: $fileName", e)
   127            }
   128        }
   129    }
   130
   131    private fun initializeMap(savedInstanceState: Bundle?) {
   132        mapView?.onCreate(savedInstanceState)
   133        mapView?.getMapAsync { maplibreMap ->
   134            this.map = maplibreMap
   135            configureUiSettings(maplibreMap)
   136            val styleJson = """
   137                {
   138                    "version": 8,
   139                    "name": "OSRS Map Style",
   140                    "sources": {},
   141                    "layers": [
   142                        { "id": "background", "type": "background", "paint": { "background-color": "#000000" } }
   143                    ]
   144                }
   145            """.trimIndent()
   146            maplibreMap.setStyle(Style.Builder().fromJson(styleJson)) { style ->
   147                val gameLon = arguments?.getString(ARG_LON)?.toDoubleOrNull()
   148                val gameLat = arguments?.getString(ARG_LAT)?.toDoubleOrNull()
   149                val zoom = 6.0
   150                val cameraBuilder = CameraPosition.Builder()
   151                if (gameLon != null && gameLat != null) {
   152                    val target = gameToLatLng(gameLon, gameLat)
   153                    cameraBuilder.target(target).zoom(zoom)
   154                } else {
   155                    cameraBuilder.target(LatLng(DEFAULT_LAT, DEFAULT_LON)).zoom(DEFAULT_ZOOM)
   156                }
   157                maplibreMap.cameraPosition = cameraBuilder.build()
   158                setMapBounds(maplibreMap)
   159                setupMapLayers(style)
   160                setupFloorControls()
   161            }
   162        }
   163    }
   164
   165    private fun configureUiSettings(map: MapLibreMap) {
   166        val uiSettings = map.uiSettings
   167        uiSettings.isLogoEnabled = false
   168        uiSettings.isAttributionEnabled = false
   169        val marginInDp = 16f
   170        val marginInPx = (marginInDp * resources.displayMetrics.density).toInt()
   171        uiSettings.setCompassGravity(Gravity.TOP or Gravity.END)
   172        uiSettings.setCompassMargins(0, marginInPx, marginInPx, 0)
   173    }
   174
   175    private fun setMapBounds(map: MapLibreMap) {
   176        val north = 85.0511
   177        val west = -180.0
   178        val south = -85.0511
   179        val east = 180.0
   180        val bounds = LatLngBounds.Builder()
   181            .include(LatLng(north, west))
   182            .include(LatLng(south, east))
   183            .build()
   184        map.setLatLngBoundsForCameraTarget(bounds)
   185    }
   186
   187    private fun setupMapLayers(style: Style) {
   188        for (floor in 0..maxFloor) {
   189            val sourceId = "osrs-source-$floor"
   190            val layerId = "osrs-layer-$floor"
   191            val fileName = mapFiles[floor]
   192            val mbtilesFile = File(requireContext().filesDir, fileName)
   193            val mbtilesUri = "mbtiles://${mbtilesFile.absolutePath}"
   194            val rasterSource = RasterSource(sourceId, mbtilesUri)
   195            style.addSource(rasterSource)
   196            val rasterLayer = RasterLayer(layerId, sourceId).withProperties(
   197                PropertyFactory.rasterResampling(Property.RASTER_RESAMPLING_NEAREST),
   198                PropertyFactory.visibility(if (floor == currentFloor) Property.VISIBLE else Property.NONE)
   199            )
   200            style.addLayer(rasterLayer)
   201        }
   202    }
   203
   204    private fun setupFloorControls() {
   205        val isEmbeddedMap = arguments?.getString(ARG_LON) != null
   206        if (isEmbeddedMap) {
   207            binding.floorControls.visibility = View.GONE
   208            return
   209        }
   210        if (maxFloor > 0) {
   211            binding.floorControls.visibility = View.VISIBLE
   212            
   213            // Apply font to floor control text
   214            binding.floorControlText.applyRubikUILabel()
   215            
   216            updateFloorControlStates()
   217            binding.floorControlUp.setOnClickListener {
   218                if (currentFloor < maxFloor) showFloor(currentFloor + 1)
   219            }
   220            binding.floorControlDown.setOnClickListener {
   221                if (currentFloor > 0) showFloor(currentFloor - 1)
   222            }
   223        } else {
   224            binding.floorControls.visibility = View.GONE
   225        }
   226    }
   227
   228    private fun showFloor(newFloor: Int) {
   229        if (newFloor == currentFloor || newFloor < 0 || newFloor > maxFloor) return
   230        val style = map?.style ?: return
   231        for (floor in 0..maxFloor) {
   232            val layer = style.getLayer("osrs-layer-$floor") as? RasterLayer
   233            layer?.let {
   234                when {
   235                    floor == newFloor -> it.setProperties(
   236                        PropertyFactory.visibility(Property.VISIBLE),
   237                        PropertyFactory.rasterOpacity(1.0f)
   238                    )
   239                    floor == 0 && newFloor > 0 -> it.setProperties(
   240                        PropertyFactory.visibility(Property.VISIBLE),
   241                        PropertyFactory.rasterOpacity(GROUND_FLOOR_UNDERLAY_OPACITY)
   242                    )
   243                    else -> it.setProperties(PropertyFactory.visibility(Property.NONE))
   244                }
   245            }
   246        }
   247        currentFloor = newFloor
   248        updateFloorControlStates()
   249    }
   250
   251    private fun updateFloorControlStates() {
   252        binding.floorControlText.text = currentFloor.toString()
   253        binding.floorControlUp.isEnabled = currentFloor < maxFloor
   254        binding.floorControlDown.isEnabled = currentFloor > 0
   255    }
   256
   257    override fun onDestroyView() {
   258        super.onDestroyView()
   259        mapView?.onDestroy()
   260        _binding = null
   261        map = null
   262    }
   263
   264    override fun onThemeChanged() {
   265        L.d("MapFragment: onThemeChanged called")
   266        // Re-apply theme attributes to floor control elements
   267        refreshThemeAttributes()
   268    }
   269
   270    private fun refreshThemeAttributes() {
   271        if (_binding != null && binding.floorControls.visibility == View.VISIBLE) {
   272            // Get the current theme's text color attributes
   273            val typedValue = android.util.TypedValue()
   274            val theme = requireContext().theme
   275            
   276            // Update floor control text color to use theme-appropriate color
   277            theme.resolveAttribute(com.omiyawaki.osrswiki.R.attr.primary_text_color, typedValue, true)
   278            if (typedValue.resourceId != 0) {
   279                val textColor = requireContext().getColor(typedValue.resourceId)
   280                binding.floorControlText.setTextColor(textColor)
   281            }
   282            
   283            L.d("MapFragment: Theme attributes refreshed for floor controls")
   284        }
   285    }
   286
   287    override fun onStart() { super.onStart(); mapView?.onStart() }
   288    override fun onResume() { super.onResume(); mapView?.onResume() }
   289    override fun onPause() { super.onPause(); mapView?.onPause() }
   290    override fun onStop() { super.onStop(); mapView?.onStop() }
   291    override fun onSaveInstanceState(outState: Bundle) { super.onSaveInstanceState(outState); mapView?.onSaveInstanceState(outState) }
   292    override fun onLowMemory() { super.onLowMemory(); mapView?.onLowMemory() }
   293
   294    override fun onHiddenChanged(hidden: Boolean) {
   295        super.onHiddenChanged(hidden)
   296        if (hidden) {
   297            mapView?.onStop()
   298        } else {
   299            mapView?.onStart()
   300        }
   301    }
   302}
