    19import com.omiyawaki.osrswiki.theme.ThemeAware
    20
    21abstract class BaseActivity : AppCompatActivity() {
    22
    23    private var currentThemeId: Int = 0
    24    private var isApplyingTheme: Boolean = false
    25
    26    override fun onCreate(savedInstanceState: Bundle?) {
    27        // Apply the selected theme BEFORE super.onCreate() and setContentView().
    28        applyCurrentTheme()
    29        super.onCreate(savedInstanceState)
    30    }
    31
    32    override fun onResume() {
    33        super.onResume()
    34        // When resuming, check if the global theme has changed since this activity
    35        // was last created. If so, apply the new theme dynamically without recreation.
    36        val globalThemeId = (application as OSRSWikiApp).getCurrentTheme().resourceId
    37        if (currentThemeId != 0 && currentThemeId != globalThemeId) {
    38            android.util.Log.d("BaseActivity", "Theme changed during resume, applying dynamically")
    39            applyThemeDynamically()
    40        }
    41    }
    42
    43    override fun onConfigurationChanged(newConfig: Configuration) {
    44        super.onConfigurationChanged(newConfig)
    45        // Check if the UI mode has changed (e.g., system light/dark theme switch).
    46        // If the app's theme is set to "auto", apply the new system-driven theme dynamically.
    47        if (Prefs.appThemeMode == "auto") {
    48            android.util.Log.d("BaseActivity", "Configuration changed with auto theme, applying dynamically")
    49            // The theme is resolved in OSRSWikiApp.getCurrentTheme() which reads the system
    50            // state, so we can apply it dynamically without recreation.
    51            applyThemeDynamically()
    52        }
    53    }
    54
    55    private fun applyCurrentTheme() {
    56        val osrsWikiApp = applicationContext as OSRSWikiApp
    57        val selectedTheme = osrsWikiApp.getCurrentTheme()
    58        // Cache the ID of the theme being applied to this activity instance.
