    80    /**
    81     * Applies a new theme dynamically without recreating the activity.
    82     * This method updates the theme and refreshes UI elements to prevent FOUC.
    83     */
    84    fun applyThemeDynamically() {
    85        // Guard against multiple simultaneous theme applications
    86        if (isApplyingTheme) {
    87            android.util.Log.d("BaseActivity", "Theme application already in progress, skipping duplicate call")
    88            return
    89        }
    90        
    91        val osrsWikiApp = applicationContext as OSRSWikiApp
    92        val newTheme = osrsWikiApp.getCurrentTheme()
    93        val newThemeId = newTheme.resourceId
    94        
    95        // Only apply if theme actually changed
    96        if (currentThemeId != newThemeId) {
    97            // Set guard flag
    98            isApplyingTheme = true
    99            
   100            try {
   101                android.util.Log.d("BaseActivity", "Applying theme dynamically from ${currentThemeId} to ${newThemeId}")
   102                
   103                // PHASE 1: Clear previous theme state
   104                clearPreviousThemeState()
   105                
   106                // PHASE 2: Update theme ID and apply new theme
   107                currentThemeId = newThemeId
   108                setTheme(newThemeId)
   109                
   110                // PHASE 3: Allow theme to take effect before refreshing views
   111                runOnUiThread {
   112                    // Post to ensure setTheme() has taken effect
   113                    window.decorView.post {
   114                        try {
   115                            // Refresh theme-dependent UI elements
   116                            refreshThemeDependentElements()
   117                            
   118                            // Notify fragments of theme change
   119                            notifyFragmentsOfThemeChange()
   120                            
   121                            android.util.Log.d("BaseActivity", "Dynamic theme application completed")
   122                        } finally {
   123                            // Clear guard flag after theme application is complete
   124                            isApplyingTheme = false
   125                        }
   126                    }
   127                }
   128            } catch (e: Exception) {
   129                android.util.Log.e("BaseActivity", "Error during theme application", e)
   130                // Clear guard flag if exception occurs
   131                isApplyingTheme = false
   132            }
   133        }
   134    }
