   210    /**
   211     * Forces refresh of cached UI elements that might not respond to normal theme updates.
   212     * This is a comprehensive approach to handle edge cases and stubborn cached elements.
   213     */
   214    private fun forceCachedUIElementRefresh(view: View) {
   215        try {
   216            // Special handling for search bar - now that we properly set hint with ColorStateList,
   217            // we can safely invalidate to ensure proper redraw (Expert 1's recommendation)
   218            if (view is TextView && view.id == com.omiyawaki.osrswiki.R.id.toolbar_search_container) {
   219                // Both layout and invalidate are needed for complete refresh
   220                view.requestLayout()
   221                view.invalidate()
   222                android.util.Log.d("BaseActivity", "Full refresh for search bar with proper invalidation")
   223                return
   224            }
   225            
   226            // Force invalidate the view and all its children recursively
   227            invalidateViewRecursively(view)
   228            
   229            // Handle specific view types that are known to cache styling
   230            when (view) {
   231                is androidx.recyclerview.widget.RecyclerView -> {
   232                    // Force adapter refresh for RecyclerViews
   233                    view.adapter?.notifyDataSetChanged()
   234                    view.invalidateItemDecorations()
   235                    android.util.Log.d("BaseActivity", "Forced RecyclerView refresh")
   236                }
   237                is androidx.viewpager2.widget.ViewPager2 -> {
   238                    // Force ViewPager2 refresh
   239                    view.adapter?.notifyDataSetChanged()
   240                    android.util.Log.d("BaseActivity", "Forced ViewPager2 refresh")
   241                }
   242                is androidx.fragment.app.FragmentContainerView -> {
   243                    // Handle fragment container views
   244                    view.invalidate()
   245                    view.requestLayout()
   246                    android.util.Log.d("BaseActivity", "Forced FragmentContainerView refresh")
   247                }
   248            }
   249            
   250            // If this is a ViewGroup, also handle special ViewGroup types
   251            if (view is ViewGroup) {
   252                when (view.javaClass.simpleName) {
   253                    "ConstraintLayout", "LinearLayout", "FrameLayout", "RelativeLayout" -> {
   254                        // Force layout refresh for common layout types
   255                        view.requestLayout()
   256                    }
   257                    "AppBarLayout", "CollapsingToolbarLayout" -> {
   258                        // Force refresh for Material Design layout components
   259                        view.invalidate()
   260                        view.requestLayout()
   261                        android.util.Log.d("BaseActivity", "Forced Material layout refresh: ${view.javaClass.simpleName}")
   262                    }
   263                }
   264                
   265                // Recursively apply to all children
   266                for (i in 0 until view.childCount) {
   267                    forceCachedUIElementRefresh(view.getChildAt(i))
   268                }
   269            }
   270            
   271        } catch (e: Exception) {
   272            android.util.Log.w("BaseActivity", "Error in forceCachedUIElementRefresh for ${view.javaClass.simpleName}", e)
   273        }
   274    }
