   343    private fun refreshTextViewTheme(textView: TextView, theme: android.content.res.Resources.Theme) {
   344        val typedValue = TypedValue()
   345        
   346        // Check if this is the search bar TextView first - handle it specially
   347        val isSearchBarTextView = textView.id == com.omiyawaki.osrswiki.R.id.toolbar_search_container
   348        
   349        if (isSearchBarTextView) {
   350            // For search bar, ONLY handle hint - don't change text color as it has proper styling from SearchBarText style
   351            // The SearchBarText style already sets textColor and textColorHint to ?android:attr/textColorSecondary
   352            
   353            // ALWAYS ensure search bar has hint text
   354            textView.hint = textView.context.getString(com.omiyawaki.osrswiki.R.string.page_toolbar_search_hint)
   355            
   356            // Re-resolve hint color from current theme using proper ColorStateList (Expert 2's recommendation)
   357            val attrs = intArrayOf(android.R.attr.textColorHint)
   358            val ta = theme.obtainStyledAttributes(attrs)
   359            val hintColorStateList = ta.getColorStateList(0)
   360            ta.recycle()
   361            
   362            if (hintColorStateList != null) {
   363                textView.setHintTextColor(hintColorStateList)
   364                android.util.Log.d("BaseActivity", "Set search bar hint text and ColorStateList from theme")
   365            } else {
   366                // Fallback to textColorSecondary if textColorHint is not available
   367                val hintTypedValue = TypedValue()
   368                if (theme.resolveAttribute(android.R.attr.textColorSecondary, hintTypedValue, true)) {
   369                    textView.setHintTextColor(hintTypedValue.data)
   370                    android.util.Log.d("BaseActivity", "Set search bar hint with fallback textColorSecondary")
   371                }
   372            }
   373            
   374            // Critical: Force view to invalidate and redraw with new hint color (Expert 1's recommendation)
   375            textView.invalidate()
   376            android.util.Log.d("BaseActivity", "Invalidated search bar to force hint redraw")
   377            
   378            return // Skip the rest of the text color logic for search bar
   379        }
   380        
   381        // For non-search bar TextViews, proceed with normal text color refresh
   382        
   383        // Preserve original hint text and colors
   384        val originalHint = textView.hint
   385        val originalHintColors = textView.hintTextColors
   386        
   387        // Check if this TextView has specific styling that should be preserved
   388        val isPreferenceText = isPreferenceTextView(textView)
   389        
   390        // Choose appropriate color attributes based on TextView type and context
   391        val textColorAttrs = when {
   392            isPreferenceText -> arrayOf(
   393                com.google.android.material.R.attr.colorOnSurface,      // Primary preference text
   394                android.R.attr.textColorPrimary,
   395                R.attr.primary_text_color
   396            )
   397            else -> arrayOf(
   398                // Comprehensive text color attribute list for general TextViews
   399                com.google.android.material.R.attr.colorOnSurface,          // Used in styles.xml, bottom nav, toolbar
   400                com.google.android.material.R.attr.colorOnSurfaceVariant,   // Used in activity_request_feature.xml, feedback
   401                
   402                // App-specific text colors
   403                R.attr.primary_text_color,              // Used in view_history_search_card.xml, map fragment
   404                R.attr.secondary_text_color,            // Used extensively for secondary text
   405                
   406                // Error and special states  
   407                com.google.android.material.R.attr.colorError,              // Used in fragment_search_results.xml
   408                com.google.android.material.R.attr.colorOnError,            // Used for error text
   409                com.google.android.material.R.attr.colorOnPrimary,          // Used for text on primary backgrounds
   410                com.google.android.material.R.attr.colorOnSecondary,        // Used for text on secondary backgrounds  
   411                com.google.android.material.R.attr.colorOnSecondaryContainer, // Used in activity_page.xml
   412                
   413                // Standard fallbacks
   414                android.R.attr.textColorPrimary,
   415                android.R.attr.textColor
   416            )
   417        }
   418        
   419        for (attr in textColorAttrs) {
   420            if (theme.resolveAttribute(attr, typedValue, true)) {
   421                try {
   422                    textView.setTextColor(typedValue.data)
   423                    
   424                    // Handle hint restoration for regular TextViews
   425                    if (originalHint != null) {
   426                        textView.hint = originalHint
   427                        if (originalHintColors != null) {
   428                            textView.setHintTextColor(originalHintColors)
   429                        }
   430                    }
   431                    
   432                    android.util.Log.d("BaseActivity", "Applied ${getAttributeName(attr)} color to ${textView.javaClass.simpleName}")
   433                    break // Use the first one that resolves
   434                } catch (e: Exception) {
   435                    continue
   436                }
   437            }
   438        }
   439    }
