   648                    // This view programmatically resolves colorOnSurfaceVariant
   649                    refreshDottedLineView(view, theme)
   650                }
   651            }
   652            
   653            // Handle Material Design components that need special refresh
   654            when (view) {
   655                is com.google.android.material.card.MaterialCardView -> {
   656                    refreshMaterialCardView(view, theme)
   657                }
   658                is com.google.android.material.button.MaterialButton -> {
   659                    refreshMaterialButton(view, theme)
   660                }
   661            }
   662            
   663            // Handle views with selectable backgrounds (ripple effects)
   664            refreshSelectableBackground(view, theme)
   665            
   666            // Force invalidation for views that might cache colors
   667            view.invalidate()
   668            
   669        } catch (e: Exception) {
   670            // Ignore errors for complex view refresh attempts
   671            android.util.Log.w("BaseActivity", "Error in refreshComplexViewTypes for ${view.javaClass.simpleName}", e)
   672        }
   673    }
   674    
   675    private fun refreshOSRSWikiCardView(view: View, theme: android.content.res.Resources.Theme) {
   676        try {
   677            val typedValue = TypedValue()
   678            if (theme.resolveAttribute(com.google.android.material.R.attr.colorSurfaceVariant, typedValue, true)) {
   679                // Force the custom view to refresh its background color
   680                view.setBackgroundColor(typedValue.data)
   681                android.util.Log.d("BaseActivity", "Refreshed OSRSWikiCardView with colorSurfaceVariant")
   682            }
   683        } catch (e: Exception) {
   684            android.util.Log.w("BaseActivity", "Failed to refresh OSRSWikiCardView", e)
   685        }
   686    }
   687    
   688    private fun refreshDottedLineView(view: View, theme: android.content.res.Resources.Theme) {
   689        try {
   690            val typedValue = TypedValue()
   691            if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurfaceVariant, typedValue, true)) {
   692                // Force the custom view to refresh - it needs to re-read the theme
   693                view.invalidate()
   694                android.util.Log.d("BaseActivity", "Refreshed DottedLineView invalidation for colorOnSurfaceVariant")
   695            }
   696        } catch (e: Exception) {
   697            android.util.Log.w("BaseActivity", "Failed to refresh DottedLineView", e)
   698        }
   699    }
   700    
   701    private fun refreshMaterialCardView(cardView: com.google.android.material.card.MaterialCardView, theme: android.content.res.Resources.Theme) {
   702        try {
   703            val typedValue = TypedValue()
   704            // Try different card background attributes
   705            val cardBackgroundAttrs = arrayOf(
   706                com.google.android.material.R.attr.colorSurfaceVariant,     // Most common for cards
   707                com.google.android.material.R.attr.colorSurface,            // Alternative
   708                R.attr.paper_color                      // App-specific fallback
   709            )
   710            
   711            for (attr in cardBackgroundAttrs) {
   712                if (theme.resolveAttribute(attr, typedValue, true)) {
   713                    cardView.setCardBackgroundColor(typedValue.data)
   714                    break
   715                }
   716            }
   717        } catch (e: Exception) {
   718            android.util.Log.w("BaseActivity", "Failed to refresh MaterialCardView", e)
   719        }
   720    }
   721    
   722    private fun refreshMaterialButton(button: com.google.android.material.button.MaterialButton, theme: android.content.res.Resources.Theme) {
   723        try {
   724            // Force button to re-read theme attributes by triggering internal refresh
   725            button.invalidate()
   726        } catch (e: Exception) {
   727            android.util.Log.w("BaseActivity", "Failed to refresh MaterialButton", e)
   728        }
   729    }
   730    
   731    private fun refreshSelectableBackground(view: View, theme: android.content.res.Resources.Theme) {
   732        try {
   733            val typedValue = TypedValue()
   734            val background = view.background
   735            
   736            // Check if this view uses selectable item backgrounds (common in layouts)
   737            val selectableAttrs = arrayOf(
   738                android.R.attr.selectableItemBackground,
   739                android.R.attr.selectableItemBackgroundBorderless
   740            )
   741            
   742            for (attr in selectableAttrs) {
   743                if (theme.resolveAttribute(attr, typedValue, true)) {
   744                    // Only apply if the view doesn't already have a complex background
   745                    if (background == null || shouldUpdateBackground(background)) {
   746                        val drawable = resources.getDrawable(typedValue.resourceId, theme)
   747                        view.background = drawable
   748                        break
   749                    }
   750                }
   751            }
   752        } catch (e: Exception) {
   753            // Ignore errors for selectable background refresh
   754        }
   755    }
   756    
   757    private fun shouldUpdateBackground(background: Drawable?): Boolean {
   758        // Allow background updates for most cases, but be more selective
   759        // This prevents overriding important custom drawables while still updating theme colors
   760        return background?.constantState == null || 
   761               background.javaClass.simpleName.contains("ColorDrawable") ||
   762               background.javaClass.simpleName.contains("GradientDrawable") ||
   763               background.javaClass.simpleName.contains("RippleDrawable")
   764    }
   765    
   766    /**
   767     * Forces refresh of theme-dependent drawables by re-inflating them.
   768     * This is critical for views using drawable resources with theme attributes.
   769     */
   770    private fun refreshThemeDependentDrawables(view: View) {
   771        try {
   772            // List of known drawable resources that use theme attributes
   773            val themeDrawableIds = arrayOf(
   774                R.drawable.shape_search_box,              // Uses ?attr/colorSurfaceVariant
   775                R.drawable.search_text_view_rounded_background, // Likely uses theme colors
   776                android.R.attr.selectableItemBackground,  // System drawable with theme colors
   777                android.R.attr.selectableItemBackgroundBorderless // System drawable with theme colors
   778            )
   779            
   780            val currentBackground = view.background
   781            
   782            // Check if the view's background might be one of our theme-dependent drawables
   783            if (currentBackground != null && shouldForceDrawableRefresh(currentBackground)) {
   784                android.util.Log.d("BaseActivity", "Attempting to refresh theme drawable for view ${view.javaClass.simpleName}")
   785                
   786                // Try to determine the original drawable resource and re-apply it
   787                refreshDrawableFromTag(view) || refreshCommonThemeDrawables(view)
   788            }
   789            
   790        } catch (e: Exception) {
   791            android.util.Log.w("BaseActivity", "Error refreshing theme-dependent drawables", e)
   792        }
   793    }
   794    
   795    /**
   796     * Checks if a drawable should be force-refreshed for theme changes.
   797     */
   798    private fun shouldForceDrawableRefresh(drawable: Drawable): Boolean {
   799        val drawableClass = drawable.javaClass.simpleName
   800        return drawableClass.contains("RippleDrawable") ||
   801               drawableClass.contains("StateListDrawable") ||
   802               drawableClass.contains("GradientDrawable") ||
   803               drawable.constantState != null // Drawable from resources
   804    }
   805    
   806    /**
   807     * Attempts to refresh drawable using view tags (if available).
   808     */
   809    private fun refreshDrawableFromTag(view: View): Boolean {
   810        return try {
   811            val tag = view.tag
   812            if (tag is Int) {
   813                // Tag contains drawable resource ID
   814                val drawable = resources.getDrawable(tag, theme)
   815                view.background = drawable
   816                android.util.Log.d("BaseActivity", "Refreshed drawable from tag: $tag")
   817                true
   818            } else false
   819        } catch (e: Exception) {
   820            false
   821        }
   822    }
   823    
   824    /**
   825     * Attempts to refresh common theme-dependent drawables by resource ID.
   826     */
   827    private fun refreshCommonThemeDrawables(view: View): Boolean {
   828        return try {
   829            // Check if this looks like a search box (common case mentioned by user)
   830            val parentClass = view.parent?.javaClass?.simpleName ?: ""
   831            val viewId = try { 
   832                resources.getResourceEntryName(view.id)
   833            } catch (e: Exception) { 
   834                "" 
   835            }
   836            
   837            when {
   838                viewId.contains("search", ignoreCase = true) -> {
   839                    // Try to apply search box drawable
   840                    val drawable = resources.getDrawable(R.drawable.shape_search_box, theme)
   841                    view.background = drawable
   842                    android.util.Log.d("BaseActivity", "Applied search box drawable to view: $viewId")
   843                    true
   844                }
   845                else -> false
   846            }
   847        } catch (e: Exception) {
   848            false
   849        }
   850    }
   851}

