    18class AppearanceSettingsFragment : PreferenceFragmentCompat(), ThemeAware {
    19
    20    override fun onCreatePreferences(savedInstanceState: Bundle?, rootKey: String?) {
    21        setPreferencesFromResource(R.xml.preferences_appearance, rootKey)
    22
    23        val appThemeModePref = findPreference<ListPreference>(Prefs.KEY_APP_THEME_MODE)
    24
    25        // Theme change listener for app theme mode preference.
    26        val themeChangeListener = Preference.OnPreferenceChangeListener { _, newValue ->
    27            L.d("AppearanceSettingsFragment: Theme change requested to: $newValue")
    28            
    29            // Post the theme change to ensure preference is persisted first
    30            view?.post {
    31                // Send broadcast to notify all activities about theme change
    32                // This will trigger broadcast receivers in all activities (including current one)
    33                // which will call BaseActivity.applyThemeDynamically() and trigger onThemeChanged() on ThemeAware fragments
    34                notifyGlobalThemeChange()
    35            }
    36            
    37            // Return true to allow the preference change to be saved.
    38            true
    39        }
    40
    41        // Assign the listener to the app theme mode preference.
    42        appThemeModePref?.onPreferenceChangeListener = themeChangeListener
    43    }
    44
    45    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
    46        super.onViewCreated(view, savedInstanceState)
    47        setupFonts()
    48    }
    49
    50    private fun setupFonts() {
    51        L.d("AppearanceSettingsFragment: Setting up fonts...")
    52        
    53        // Apply fonts to preference TextViews by traversing the RecyclerView
    54        val recyclerView = listView as? RecyclerView
    55        recyclerView?.post {
    56            applyFontsToPreferences(recyclerView)
    57        }
    58    }
    59
    60    private fun applyFontsToPreferences(recyclerView: RecyclerView) {
    61        for (i in 0 until recyclerView.childCount) {
    62            val itemView = recyclerView.getChildAt(i)
    63            applyFontsToPreferenceItem(itemView)
    64        }
    65        
    66        // Also set up a scroll listener to apply fonts to newly visible items
    67        recyclerView.addOnScrollListener(object : RecyclerView.OnScrollListener() {
    68            override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
    69                super.onScrolled(recyclerView, dx, dy)
    70                for (i in 0 until recyclerView.childCount) {
    71                    val itemView = recyclerView.getChildAt(i)
    72                    applyFontsToPreferenceItem(itemView)
    73                }
    74            }
    75        })
    76        
    77        L.d("AppearanceSettingsFragment: Fonts applied to preference items")
    78    }
    79
    80    private fun applyFontsToPreferenceItem(itemView: View) {
    81        // Let preference text use system fonts - no custom font application needed
    82        // This aligns with the app's migration to system fonts for non-heading text
    83    }
    84
    85    override fun onThemeChanged() {
    86        if (!isAdded || view == null) {
    87            return
    88        }
    89        
    90        L.d("AppearanceSettingsFragment: onThemeChanged called - refreshing preferences UI")
    91        
    92        // Use the established ThemeAware callback to refresh the fragment UI
    93        refreshPreferencesThemeDirectly()
    94    }
    95    
    96    private fun refreshPreferencesThemeDirectly() {
    97        try {
    98            L.d("AppearanceSettingsFragment: Refreshing preferences theme directly")
    99            
   100            // Refresh the fragment's root view background
   101            view?.let { fragmentView ->
   102                val typedValue = android.util.TypedValue()
   103                val theme = requireContext().theme
   104                
   105                // Apply new background color
   106                val backgroundAttrs = arrayOf(
   107                    com.omiyawaki.osrswiki.R.attr.paper_color,
   108                    com.google.android.material.R.attr.colorSurface,
   109                    android.R.attr.colorBackground
   110                )
   111                
   112                for (attr in backgroundAttrs) {
   113                    if (theme.resolveAttribute(attr, typedValue, true)) {
   114                        fragmentView.setBackgroundColor(typedValue.data)
   115                        L.d("AppearanceSettingsFragment: Applied fragment background color")
   116                        break
   117                    }
   118                }
   119            }
   120            
   121            // Refresh the RecyclerView and preference items using proper preference refresh
   122            val recyclerView = listView as? RecyclerView
   123            recyclerView?.let { rv ->
   124                // Apply new theme background to the RecyclerView
   125                val typedValue = android.util.TypedValue()
   126                val theme = requireContext().theme
   127                
   128                if (theme.resolveAttribute(com.omiyawaki.osrswiki.R.attr.paper_color, typedValue, true)) {
   129                    rv.setBackgroundColor(typedValue.data)
   130                }
   131                
   132                // Force adapter refresh to pick up new theme colors - this is critical for preferences
   133                rv.adapter?.notifyDataSetChanged()
   134                
   135                // Force complete refresh
   136                rv.invalidate()
   137                rv.requestLayout()
   138                
   139                L.d("AppearanceSettingsFragment: RecyclerView refreshed with new theme")
   140                
   141                // CRITICAL: Apply theme colors to preference text views
   142                // PreferenceFragmentCompat uses complex view hierarchies that generic theme refresh misses
   143                refreshPreferenceTextViews(rv, theme)
   144            }
   145            
   146            L.d("AppearanceSettingsFragment: Preferences theme refresh completed successfully")
   147            
   148        } catch (e: Exception) {
   149            L.e("AppearanceSettingsFragment: Error refreshing preferences theme directly: ${e.message}")
   150        }
   151    }
   152    
   153    private fun refreshPreferenceTextViews(recyclerView: RecyclerView, theme: android.content.res.Resources.Theme) {
   154        try {
   155            L.d("AppearanceSettingsFragment: Refreshing preference text views with new theme")
   156            
   157            val typedValue = android.util.TypedValue()
   158            
   159            // Resolve theme colors for preferences
   160            val primaryTextColor = if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurface, typedValue, true)) {
   161                typedValue.data
   162            } else if (theme.resolveAttribute(android.R.attr.textColorPrimary, typedValue, true)) {
   163                typedValue.data
   164            } else {
   165                null
   166            }
   167            
   168            val secondaryTextColor = if (theme.resolveAttribute(android.R.attr.textColorSecondary, typedValue, true)) {
   169                typedValue.data
   170            } else if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurfaceVariant, typedValue, true)) {
   171                typedValue.data
   172            } else {
   173                null
   174            }
   175            
   176            // Traverse all visible preference items
   177            for (i in 0 until recyclerView.childCount) {
   178                val itemView = recyclerView.getChildAt(i)
   179                refreshPreferenceItemTextViews(itemView, primaryTextColor, secondaryTextColor)
   180            }
   181            
   182            // Also set up a scroll listener to refresh newly visible items
   183            // Remove existing listeners to avoid duplicates
   184            val existingListeners = mutableListOf<RecyclerView.OnScrollListener>()
   185            try {
   186                // Unfortunately there's no clean way to remove specific listeners, so we'll add ours
   187                recyclerView.addOnScrollListener(object : RecyclerView.OnScrollListener() {
   188                    override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
   189                        super.onScrolled(recyclerView, dx, dy)
   190                        // Refresh any newly visible preference items
   191                        for (j in 0 until recyclerView.childCount) {
   192                            val itemView = recyclerView.getChildAt(j)
   193                            refreshPreferenceItemTextViews(itemView, primaryTextColor, secondaryTextColor)
   194                        }
   195                    }
   196                })
   197            } catch (e: Exception) {
   198                L.w("AppearanceSettingsFragment: Could not set scroll listener: ${e.message}")
   199            }
   200            
   201            L.d("AppearanceSettingsFragment: Preference text views refreshed")
   202            
   203        } catch (e: Exception) {
   204            L.e("AppearanceSettingsFragment: Error refreshing preference text views: ${e.message}")
   205        }
   206    }
   207    
   208    private fun refreshPreferenceItemTextViews(itemView: android.view.View, primaryTextColor: Int?, secondaryTextColor: Int?) {
   209        try {
   210            // Find preference title (primary text) - usually android.R.id.title
   211            val titleView = itemView.findViewById<android.widget.TextView>(android.R.id.title)
   212            titleView?.let { textView ->
   213                primaryTextColor?.let { color ->
   214                    textView.setTextColor(color)
   215                    L.d("AppearanceSettingsFragment: Applied primary color to preference title: ${textView.text}")
   216                }
   217            }
   218            
   219            // Find preference summary (secondary text) - usually android.R.id.summary
   220            val summaryView = itemView.findViewById<android.widget.TextView>(android.R.id.summary)
   221            summaryView?.let { textView ->
   222                secondaryTextColor?.let { color ->
   223                    textView.setTextColor(color)
   224                    L.d("AppearanceSettingsFragment: Applied secondary color to preference summary: ${textView.text}")
   225                }
   226            }
   227            
   228            // Also check for any other TextViews that might be in the preference layout
   229            if (itemView is android.view.ViewGroup) {
   230                refreshTextViewsInViewGroup(itemView, primaryTextColor, secondaryTextColor)
   231            }
   232            
   233        } catch (e: Exception) {
   234            L.w("AppearanceSettingsFragment: Error refreshing preference item: ${e.message}")
   235        }
   236    }
   237    
   238    private fun refreshTextViewsInViewGroup(viewGroup: android.view.ViewGroup, primaryTextColor: Int?, secondaryTextColor: Int?) {
   239        for (i in 0 until viewGroup.childCount) {
   240            val child = viewGroup.getChildAt(i)
   241            when (child) {
   242                is android.widget.TextView -> {
   243                    // Skip if we already handled this TextView by ID
   244                    if (child.id != android.R.id.title && child.id != android.R.id.summary) {
   245                        // Apply primary color to other text views by default
   246                        primaryTextColor?.let { color ->
   247                            child.setTextColor(color)
   248                        }
   249                    }
   250                }
   251                is android.view.ViewGroup -> {
   252                    refreshTextViewsInViewGroup(child, primaryTextColor, secondaryTextColor)
   253                }
   254            }
   255        }
   256    }
   257    
   258    private fun refreshPreferencesUI() {
   259        // Refresh the preference list to apply new theme colors
   260        try {
   261            preferenceScreen?.let {
   262                // Force complete refresh of the preference RecyclerView
   263                val recyclerView = listView as? RecyclerView
   264                recyclerView?.let { rv ->
   265                    // Invalidate and request layout to force theme color refresh
   266                    rv.invalidate()
   267                    rv.requestLayout()
   268                    
   269                    // Force adapter refresh to pick up new theme colors
   270                    rv.adapter?.notifyDataSetChanged()
   271                    
   272                    // Apply new theme background color to RecyclerView with comprehensive fallback
   273                    applyNewBackgroundColor(rv)
   274                    
   275                    // Force refresh of all visible preference items
   276                    forceRefreshVisiblePreferences(rv)
   277                    
   278                    // Re-apply fonts with new theme colors
   279                    setupFonts()
   280                }
   281                
   282                L.d("AppearanceSettingsFragment: Comprehensive preferences UI refresh completed")
   283            }
   284        } catch (e: Exception) {
   285            L.e("AppearanceSettingsFragment: Error refreshing preferences UI: ${e.message}")
   286        }
   287    }
   288    
   289    private fun applyNewBackgroundColor(recyclerView: RecyclerView) {
   290        try {
   291            val typedValue = android.util.TypedValue()
   292            val theme = requireContext().theme
   293            
   294            // Try multiple background color attributes in order of preference
   295            val backgroundAttrs = arrayOf(
   296                com.omiyawaki.osrswiki.R.attr.paper_color,
   297                com.google.android.material.R.attr.colorSurface,
   298                android.R.attr.colorBackground
   299            )
   300            
   301            for (attr in backgroundAttrs) {
   302                if (theme.resolveAttribute(attr, typedValue, true)) {
   303                    // Apply to RecyclerView
   304                    recyclerView.setBackgroundColor(typedValue.data)
   305                    
   306                    // CRITICAL: Also apply to parent views to ensure complete background coverage
   307                    applyBackgroundToParentViews(recyclerView, typedValue.data)
   308                    
   309                    L.d("AppearanceSettingsFragment: Applied background color from ${getAttributeName(attr)}: ${Integer.toHexString(typedValue.data)}")
   310                    break
   311                }
   312            }
   313        } catch (e: Exception) {
   314            L.w("AppearanceSettingsFragment: Error applying background color: ${e.message}")
   315        }
   316    }
   317    
   318    private fun applyBackgroundToParentViews(view: android.view.View, backgroundColor: Int) {
   319        try {
   320            var currentView = view.parent
   321            var level = 0
   322            val maxLevels = 3 // Limit to prevent going too far up the hierarchy
   323            
   324            while (currentView != null && currentView is android.view.View && level < maxLevels) {
   325                val parentView = currentView as android.view.View
   326                
   327                // Apply background to suitable parent views
   328                if (shouldApplyBackgroundToParent(parentView)) {
   329                    parentView.setBackgroundColor(backgroundColor)
   330                    L.d("AppearanceSettingsFragment: Applied background to parent view level $level: ${parentView.javaClass.simpleName}")
   331                }
   332                
   333                currentView = parentView.parent
   334                level++
   335            }
   336        } catch (e: Exception) {
   337            L.w("AppearanceSettingsFragment: Error applying background to parent views: ${e.message}")
   338        }
   339    }
   340    
   341    private fun shouldApplyBackgroundToParent(view: android.view.View): Boolean {
   342        // Only apply background to certain types of parent views to avoid breaking other UI elements
   343        val className = view.javaClass.simpleName
   344        return className.contains("FrameLayout") || 
   345               className.contains("LinearLayout") ||
   346               className.contains("PreferenceRecyclerViewFragment") ||
   347               view.id == android.R.id.list_container ||
   348               view.id == androidx.preference.R.id.recycler_view
   349    }
   350    
   351    private fun forceFragmentBackgroundRefresh(fragmentView: android.view.View) {
   352        try {
   353            val typedValue = android.util.TypedValue()
   354            val theme = requireContext().theme
   355            
   356            // Apply new theme background color to the fragment's root view
   357            val backgroundAttrs = arrayOf(
   358                com.omiyawaki.osrswiki.R.attr.paper_color,
   359                com.google.android.material.R.attr.colorSurface,
   360                android.R.attr.colorBackground
   361            )
   362            
   363            for (attr in backgroundAttrs) {
   364                if (theme.resolveAttribute(attr, typedValue, true)) {
   365                    fragmentView.setBackgroundColor(typedValue.data)
   366                    L.d("AppearanceSettingsFragment: Applied fragment view background: ${Integer.toHexString(typedValue.data)}")
   367                    break
   368                }
   369            }
   370            
   371            // Force complete invalidation and redraw
   372            fragmentView.invalidate()
   373            fragmentView.requestLayout()
   374            
   375        } catch (e: Exception) {
   376            L.w("AppearanceSettingsFragment: Error forcing fragment background refresh: ${e.message}")
   377        }
   378    }
   379    
   380    private fun forceRefreshVisiblePreferences(recyclerView: RecyclerView) {
   381        try {
   382            // Force refresh all visible preference items
   383            for (i in 0 until recyclerView.childCount) {
   384                val child = recyclerView.getChildAt(i)
   385                child?.let { preferenceView ->
   386                    // Force complete refresh of each preference item
   387                    preferenceView.invalidate()
   388                    preferenceView.requestLayout()
   389                    
   390                    // If this preference has text views, force them to re-read theme colors
   391                    refreshTextViewsInPreference(preferenceView)
   392                }
   393            }
   394            L.d("AppearanceSettingsFragment: Forced refresh of ${recyclerView.childCount} visible preferences")
   395        } catch (e: Exception) {
   396            L.w("AppearanceSettingsFragment: Error refreshing visible preferences: ${e.message}")
   397        }
   398    }
   399    
   400    private fun refreshTextViewsInPreference(preferenceView: android.view.View) {
   401        try {
   402            if (preferenceView is android.view.ViewGroup) {
   403                for (i in 0 until preferenceView.childCount) {
   404                    val child = preferenceView.getChildAt(i)
   405                    if (child is android.widget.TextView) {
   406                        // CRITICAL: Apply actual theme colors instead of just invalidating
   407                        applyTextViewThemeColors(child)
   408                    } else if (child is android.view.ViewGroup) {
   409                        // Recursively refresh nested ViewGroups
   410                        refreshTextViewsInPreference(child)
   411                    }
   412                }
   413            }
   414        } catch (e: Exception) {
   415            // Ignore errors in preference item refresh
   416        }
   417    }
   418    
   419    private fun applyTextViewThemeColors(textView: android.widget.TextView) {
   420        try {
   421            val typedValue = android.util.TypedValue()
   422            val theme = requireContext().theme
   423            
   424            // Determine if this is a title or summary by checking text characteristics
   425            val isTitle = isPreferenceTitle(textView)
   426            
   427            if (isTitle) {
   428                // Apply primary text color for titles
   429                val titleAttrs = arrayOf(
   430                    com.google.android.material.R.attr.colorOnSurface,          // Material 3 primary
   431                    android.R.attr.textColorPrimary,                           // System primary
   432                    R.attr.primary_text_color                                  // App fallback
   433                )
   434                
   435                for (attr in titleAttrs) {
   436                    if (theme.resolveAttribute(attr, typedValue, true)) {
   437                        textView.setTextColor(typedValue.data)
   438                        L.d("AppearanceSettingsFragment: Applied title color from ${getAttributeName(attr)}")
   439                        break
   440                    }
   441                }
   442            } else {
   443                // Apply secondary text color for summaries
   444                val summaryAttrs = arrayOf(
   445                    android.R.attr.textColorSecondary,                         // System secondary (most common)
   446                    com.google.android.material.R.attr.colorOnSurfaceVariant,  // Material 3 secondary
   447                    R.attr.secondary_text_color                                // App fallback
   448                )
   449                
   450                for (attr in summaryAttrs) {
   451                    if (theme.resolveAttribute(attr, typedValue, true)) {
   452                        textView.setTextColor(typedValue.data)
   453                        L.d("AppearanceSettingsFragment: Applied summary color from ${getAttributeName(attr)}: ${textView.text}")
   454                        break
   455                    }
   456                }
   457            }
   458            
   459            // Force immediate refresh
   460            textView.invalidate()
   461            
   462        } catch (e: Exception) {
   463            L.w("AppearanceSettingsFragment: Error applying TextView theme colors: ${e.message}")
   464        }
   465    }
   466    
   467    private fun isPreferenceTitle(textView: android.widget.TextView): Boolean {
   468        // Heuristics to determine if this TextView is a title or summary
   469        val text = textView.text?.toString() ?: ""
   470        val textSize = textView.textSize
   471        val typeface = textView.typeface
   472        
   473        // Title characteristics: larger text, often bold, shorter text
   474        // Common title texts we know about
   475        val knownTitles = setOf(
   476            "App theme", 
   477            "Collapse tables"
   478        )
   479        
   480        // Summary characteristics: smaller text, longer descriptive text
   481        val knownSummaries = setOf(
   482            "Dark", 
   483            "Light", 
   484            "Auto", 
   485            "Collapses infoboxes by default in articles"
   486        )
   487        
   488        return when {
   489            knownTitles.contains(text) -> true
   490            knownSummaries.contains(text) -> false
   491            // Fallback heuristics
   492            textSize > 16 -> true  // Larger text is likely a title
   493            text.length < 20 -> true  // Short text is likely a title  
   494            text.contains("by default") || text.contains("articles") -> false // Long descriptive text is summary
   495            else -> false // Default to summary to be safe
   496        }
   497    }
   498    
   499    private fun getAttributeName(attr: Int): String {
   500        return when (attr) {
   501            com.omiyawaki.osrswiki.R.attr.paper_color -> "paper_color"
   502            com.google.android.material.R.attr.colorSurface -> "colorSurface"
   503            android.R.attr.colorBackground -> "colorBackground"
   504            else -> "unknown_attr_$attr"
   505        }
   506    }
   507    
   508    private fun notifyGlobalThemeChange() {
   509        try {
   510            // Send local broadcast to notify other activities about theme change
   511            val intent = Intent(ACTION_THEME_CHANGED)
   512            LocalBroadcastManager.getInstance(requireContext()).sendBroadcast(intent)
   513            L.d("AppearanceSettingsFragment: Global theme change broadcast sent")
   514        } catch (e: Exception) {
   515            L.e("AppearanceSettingsFragment: Error sending theme change broadcast: ${e.message}")
   516        }
   517    }
   518
   519
   520    companion object {
   521        const val TAG = "AppearanceSettingsFragment"
   522        const val ACTION_THEME_CHANGED = "com.omiyawaki.osrswiki.THEME_CHANGED"
   523        
   524        fun newInstance(): AppearanceSettingsFragment {
   525            return AppearanceSettingsFragment()
   526        }
   527    }
   528}

