    13/**
    14 * Activity to host the AppearanceSettingsFragment for appearance-specific settings.
    15 * Shows only theme, color, and display options - no offline/storage settings.
    16 */
    17class AppearanceSettingsActivity : BaseActivity() {
    18
    19    private var themeChangeReceiver: BroadcastReceiver? = null
    20
    21    companion object {
    22        /**
    23         * Creates an Intent to start AppearanceSettingsActivity.
    24         * @param context The Context to use.
    25         * @return An Intent to start AppearanceSettingsActivity.
    26         */
    27        fun newIntent(context: Context): Intent {
    28            return Intent(context, AppearanceSettingsActivity::class.java)
    29        }
    30    }
    31
    32    override fun onCreate(savedInstanceState: Bundle?) {
    33        super.onCreate(savedInstanceState)
    34        setContentView(R.layout.activity_appearance_settings)
    35
    36        if (savedInstanceState == null) {
    37            supportFragmentManager
    38                .beginTransaction()
    39                .replace(
    40                    R.id.appearance_settings_container, 
    41                    AppearanceSettingsFragment.newInstance(), 
    42                    AppearanceSettingsFragment.TAG
    43                )
    44                .commit()
    45        }
    46
    47        setupToolbar()
    48        setupThemeChangeReceiver()
    49    }
    50
    51    private fun setupToolbar() {
    52        supportActionBar?.apply {
    53            title = getString(R.string.settings_category_appearance)
    54            setDisplayHomeAsUpEnabled(true)
    55            setDisplayShowHomeEnabled(true)
    56        }
    57    }
    58
    59    override fun onSupportNavigateUp(): Boolean {
    60        onBackPressedDispatcher.onBackPressed()
    61        return true
    62    }
    63    
    64    private fun setupThemeChangeReceiver() {
    65        themeChangeReceiver = object : BroadcastReceiver() {
    66            override fun onReceive(context: Context?, intent: Intent?) {
    67                if (intent?.action == AppearanceSettingsFragment.ACTION_THEME_CHANGED) {
    68                    L.d("AppearanceSettingsActivity: Received theme change broadcast")
    69                    // For AppearanceSettings specifically, recreate the activity for complete refresh
    70                    // This ensures all preference items and backgrounds are properly themed
    71                    recreate()
    72                }
    73            }
    74        }
    75        
    76        val filter = IntentFilter(AppearanceSettingsFragment.ACTION_THEME_CHANGED)
    77        LocalBroadcastManager.getInstance(this).registerReceiver(themeChangeReceiver!!, filter)
    78        L.d("AppearanceSettingsActivity: Theme change receiver registered")
    79    }
    80    
    81    private fun unregisterThemeChangeReceiver() {
    82        themeChangeReceiver?.let { receiver ->
    83            LocalBroadcastManager.getInstance(this).unregisterReceiver(receiver)
    84            themeChangeReceiver = null
    85            L.d("AppearanceSettingsActivity: Theme change receiver unregistered")
    86        }
    87    }
    88    
    89    override fun refreshThemeDependentElements() {
    90        super.refreshThemeDependentElements()
    91        L.d("AppearanceSettingsActivity: Refreshing theme-dependent elements")
    92        
    93        try {
    94            // CRITICAL: Refresh status bar theming IMMEDIATELY and with proper timing
    95            // Status bar theming must happen early to avoid visual glitches
    96            setupStatusBarTheming()
    97            
    98            // Force a complete window refresh to apply status bar changes immediately
    99            window.decorView.invalidate()
   100            
   101            // Refresh the toolbar/action bar theme
   102            supportActionBar?.let { actionBar ->
   103                // Force toolbar colors to refresh
   104                val typedValue = android.util.TypedValue()
   105                val theme = this.theme
   106                
   107                // Apply toolbar background color
   108                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSurface, typedValue, true)) {
   109                    // The toolbar should pick up the new theme automatically, but force refresh if needed
   110                    actionBar.setBackgroundDrawable(android.graphics.drawable.ColorDrawable(typedValue.data))
   111                }
   112                
   113                // Apply toolbar text color 
   114                if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurface, typedValue, true)) {
   115                    // Unfortunately, ActionBar doesn't have a direct setTitleColor method
   116                    // The theme should handle this, but we can try to find and refresh the title TextView
   117                    try {
   118                        val titleId = resources.getIdentifier("action_bar_title", "id", "android")
   119                        if (titleId > 0) {
   120                            findViewById<android.widget.TextView>(titleId)?.setTextColor(typedValue.data)
   121                        }
   122                    } catch (e: Exception) {
   123                        L.d("AppearanceSettingsActivity: Could not manually set action bar title color: ${e.message}")
   124                    }
   125                }
   126            }
   127            
   128            // Refresh any other UI elements specific to this activity
   129            val containerView = findViewById<android.view.View>(R.id.appearance_settings_container)
   130            containerView?.let { container ->
   131                refreshViewBackground(container)
   132            }
   133            
   134            // CRITICAL: Force a second status bar refresh after other elements
   135            // This ensures status bar theming overrides any other conflicting settings
   136            window.decorView.post {
   137                setupStatusBarTheming()
   138            }
   139            
   140            L.d("AppearanceSettingsActivity: Theme-dependent elements refresh completed")
   141            
   142        } catch (e: Exception) {
   143            L.e("AppearanceSettingsActivity: Error refreshing theme elements: ${e.message}")
   144        }
   145    }
   146    
   147    private fun setupStatusBarTheming() {
   148        try {
   149            // Get the current theme's windowLightStatusBar setting
   150            val typedValue = android.util.TypedValue()
   151            val theme = this.theme
   152            val hasLightStatusBar = theme.resolveAttribute(android.R.attr.windowLightStatusBar, typedValue, true) && typedValue.data != 0
   153            
   154            // Apply the theme's status bar settings
   155            val windowInsetsController = androidx.core.view.ViewCompat.getWindowInsetsController(window.decorView)
   156            windowInsetsController?.let { controller ->
   157                controller.isAppearanceLightStatusBars = hasLightStatusBar
   158                L.d("AppearanceSettingsActivity: Set status bar light mode: $hasLightStatusBar")
   159            }
   160            
   161            // Set status bar color from theme if available
   162            val statusBarColorTypedValue = android.util.TypedValue()
   163            if (theme.resolveAttribute(android.R.attr.statusBarColor, statusBarColorTypedValue, true)) {
   164                window.statusBarColor = statusBarColorTypedValue.data
   165                L.d("AppearanceSettingsActivity: Applied theme status bar color")
   166            }
   167        } catch (e: Exception) {
   168            L.e("AppearanceSettingsActivity: Error setting up status bar theming: ${e.message}")
   169        }
   170    }
   171    
   172    private fun refreshViewBackground(view: android.view.View) {
   173        try {
   174            val typedValue = android.util.TypedValue()
   175            val theme = this.theme
   176            
   177            // Try to apply background color from theme
   178            val backgroundAttrs = arrayOf(
   179                R.attr.paper_color,
   180                com.google.android.material.R.attr.colorSurface,
   181                android.R.attr.colorBackground
   182            )
   183            
   184            for (attr in backgroundAttrs) {
   185                if (theme.resolveAttribute(attr, typedValue, true)) {
   186                    view.setBackgroundColor(typedValue.data)
   187                    L.d("AppearanceSettingsActivity: Applied background color from theme")
   188                    break
   189                }
   190            }
   191        } catch (e: Exception) {
   192            L.w("AppearanceSettingsActivity: Error refreshing view background: ${e.message}")
   193        }
   194    }
   195    
   196    override fun onDestroy() {
   197        unregisterThemeChangeReceiver()
   198        super.onDestroy()
   199    }
   200}

