/* Universal box-sizing */
* {
    box-sizing: border-box;
}

/* Basic reset for html and body */
html {
    width: 100%;
    margin: 0;
    padding: 0;
    overflow-x: hidden; /* Prevent horizontal page scrolling */
}

body {
    /* Initially hide body to prevent FOUC of raw HTML */
    visibility: hidden;
    margin: 16px;
    padding: 0;
    background-color: var(--body-main);
    color: var(--text-color);
    font-family: sans-serif;
    font-weight: 400;
    font-size: 16px;
    line-height: 1.4;
    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;
    overflow-x: hidden; /* Prevent horizontal page scrolling */
}

/* --- Base Element Styling (using CSS variables) --- */
a {
    color: var(--link-color);
    text-decoration: none;
    font-weight: 500;
}
a:visited {
    color: var(--link-visited-color);
}
a.external {
    /*
    background-image: var(--external-icon);
    background-repeat: no-repeat;
    background-position: center right;
    padding-right: 15px;
    background-size: 12px 12px;
    */
}

h1, h2, h3, h4, h5, h6 {
    color: var(--heading-color);
    font-family: 'Alegreya', 'Palatino', 'Georgia', serif;
    font-weight: bold;
    margin-top: 1.2em;
    margin-bottom: 0.6em;
    line-height: 1.3;
    border-bottom: 1px solid var(--sidebar-color);
    padding-bottom: 0.2em;
}
h1 { 
    font-size: 2em; 
    font-weight: bold;
}
h2 { 
    font-size: 1.7em; 
    font-weight: bold;
}
h3 { 
    font-size: 1.5em;
    font-weight: bold;
}
h4, h5, h6 {
    font-size: 1.2em;
    font-weight: bold;
}

/* Page title styling - ensure consistent fonts for page headings */
.mw-page-title,
.firstHeading,
.page-title,
#firstHeading,
h1.firstHeading,
h1.page-header {
    font-family: 'Alegreya', 'Palatino', 'Georgia', serif;
    font-weight: bold;
    font-size: 2.2em;
    line-height: 1.2;
    margin-bottom: 0.5em;
}

p {
    margin-bottom: 1em;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
    margin: 12px 0;
    border-radius: 3px;
    /* Prevents blurring of pixel art when scaled */
    image-rendering: -moz-crisp-edges; /* Firefox */
    image-rendering: -webkit-optimize-contrast; /* Webkit (non-standard) */
    image-rendering: crisp-edges; /* W3C draft */
    image-rendering: pixelated; /* W3C standard */
}

ul, ol {
    margin-left: 20px;
    padding-left: 0;
    margin-bottom: 1em;
}
/* Remove bottom margin from nested lists */
li > ul, li > ol {
    margin-bottom: 0;
}
ul {
    list-style-image: var(--ul-list-style-image);
}

pre {
    overflow-x: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    padding: 0.5em;
    background-color: var(--mw-code-background-color);
    border: 1px solid var(--body-border);
    border-radius: 3px;
    margin-bottom: 1em;
}
code {
    background-color: var(--mw-code-background-color);
    border: 1px solid var(--body-border);
    padding: 2px 4px;
    font-family: monospace;
    border-radius: 3px;
}

