/**
 * Debug script to log messagebox dimensions and layout properties
 */
(function() {
    'use strict';

    function logMessageboxDimensions() {
        const messageboxes = document.querySelectorAll('.messagebox');
        console.log('=== MESSAGEBOX DEBUG ===');
        console.log('Found', messageboxes.length, 'messageboxes');

        messageboxes.forEach((messagebox, index) => {
            console.log(`--- Messagebox ${index + 1} ---`);

            // Log table dimensions
            const rect = messagebox.getBoundingClientRect();
            const computedStyle = window.getComputedStyle(messagebox);

            console.log('Table dimensions:', {
                width: rect.width,
                height: rect.height,
                marginTop: computedStyle.marginTop,
                marginBottom: computedStyle.marginBottom,
                paddingTop: computedStyle.paddingTop,
                paddingBottom: computedStyle.paddingBottom
            });

            // Log cell dimensions
            const cells = messagebox.querySelectorAll('td');
            cells.forEach((cell, cellIndex) => {
                const cellRect = cell.getBoundingClientRect();
                const cellStyle = window.getComputedStyle(cell);

                console.log(`Cell ${cellIndex + 1} (${cell.className || 'no class'}):`, {
                    width: cellRect.width,
                    height: cellRect.height,
                    paddingTop: cellStyle.paddingTop,
                    paddingBottom: cellStyle.paddingBottom,
                    verticalAlign: cellStyle.verticalAlign
                });

                // Log content dimensions within each cell
                const children = Array.from(cell.children);
                children.forEach((child, childIndex) => {
                    const childRect = child.getBoundingClientRect();
                    const childStyle = window.getComputedStyle(child);

                    console.log(`  Child ${childIndex + 1} (${child.tagName}.${child.className || 'no class'}):`, {
                        width: childRect.width,
                        height: childRect.height,
                        marginTop: childStyle.marginTop,
                        marginBottom: childStyle.marginBottom,
                        paddingTop: childStyle.paddingTop,
                        paddingBottom: childStyle.paddingBottom,
                        lineHeight: childStyle.lineHeight,
                        fontSize: childStyle.fontSize
                    });
                });
            });

            console.log('--- End Messagebox', index + 1, '---');
        });

        console.log('=== END MESSAGEBOX DEBUG ===');
    }

    // Run when DOM is loaded
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', logMessageboxDimensions);
    } else {
        // DOM already loaded
        logMessageboxDimensions();
    }

    // Also run on window load for good measure
    window.addEventListener('load', function() {
        console.log('=== MESSAGEBOX DEBUG (on window load) ===');
        logMessageboxDimensions();
    });

})();

