    border-left: 5px solid var(--body-border);
    background-color: var(--body-light);
}

hr {
    border: 0;
    height: 1px;
    background-color: var(--sidebar-color);
    margin: 1em 0;
}

/* --- Hatnote Styles --- */
/* Styles for notes at the top of a page (e.g., disambiguation links) */
.hatnote {
    font-style: italic;
    margin-bottom: 0.5em;
}

